const mysql = require('mysql2/promise');
const logger = require('../utils/logger');

/**
 * ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šãƒ—ãƒ¼ãƒ«
 * æœ¬ç•ªç’°å¢ƒå‘ã‘ã«æœ€é©åŒ–ã•ã‚ŒãŸè¨­å®š
 */
const isProduction = process.env.NODE_ENV === 'production';

const pool = mysql.createPool({
  host: process.env.DB_HOST || 'localhost',
  user: process.env.DB_USER || 'server',
  password: process.env.DB_PASS || 'pass',
  database: process.env.DB_NAME || 'sotsuken',
  port: process.env.DB_PORT || 3306,
  charset: 'utf8mb4',
  timezone: '+09:00',

    // æŽ¥ç¶šãƒ—ãƒ¼ãƒ«è¨­å®š
  waitForConnections: true,
  connectionLimit: parseInt(process.env.DB_CONNECTION_LIMIT) || (isProduction ? 50 : 10),
  queueLimit: parseInt(process.env.DB_QUEUE_LIMIT) || (isProduction ? 100 : 0),

  // ã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆè¨­å®š
  connectTimeout: parseInt(process.env.DB_CONNECT_TIMEOUT) || 10 * 1000, // 10ç§’

  // ãã®ä»–è¨­å®š
  dateStrings: true,
  multipleStatements: false, // SQLã‚¤ãƒ³ã‚¸ã‚§ã‚¯ã‚·ãƒ§ãƒ³å¯¾ç­–
  ssl: process.env.DB_SSL === 'true' ? {
    rejectUnauthorized: false
  } : false
});

/**
 * ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šãƒ†ã‚¹ãƒˆ
 */
const testConnection = async () => {
  try {
    const connection = await pool.getConnection();
    logger.info('ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šã«æˆåŠŸã—ã¾ã—ãŸ');
    connection.release();
    return true;
  } catch (error) {
    logger.error('ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šã‚¨ãƒ©ãƒ¼:', error.message);
    return false;
  }
};

/**
 * ã‚¯ã‚¨ãƒªå®Ÿè¡Œï¼ˆãƒ—ãƒªãƒšã‚¢ãƒ‰ã‚¹ãƒ†ãƒ¼ãƒˆãƒ¡ãƒ³ãƒˆï¼‰
 */
const query = async (sql, params = []) => {
  try {
    const [rows] = await pool.execute(sql, params);
    return rows;
  } catch (error) {
    logger.error('ã‚¯ã‚¨ãƒªå®Ÿè¡Œã‚¨ãƒ©ãƒ¼:', {
      sql: sql.substring(0, 100) + '...',
      params: params,
      error: error.message
    });
    throw error;
  }
};

/**
 * ãƒˆãƒ©ãƒ³ã‚¶ã‚¯ã‚·ãƒ§ãƒ³å®Ÿè¡Œ
 */
const transaction = async (callback) => {
  const connection = await pool.getConnection();
  try {
    await connection.beginTransaction();
    const result = await callback(connection);
    await connection.commit();
    return result;
  } catch (error) {
    await connection.rollback();
    throw error;
  } finally {
    connection.release();
  }
};

/**
 * æŽ¥ç¶šãƒ—ãƒ¼ãƒ«ã®çµ‚äº†
 */
const closePool = async () => {
  try {
    await pool.end();
    logger.info('ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šãƒ—ãƒ¼ãƒ«ã‚’çµ‚äº†ã—ã¾ã—ãŸ');
  } catch (error) {
    logger.error('æŽ¥ç¶šãƒ—ãƒ¼ãƒ«çµ‚äº†ã‚¨ãƒ©ãƒ¼:', error.message);
  }
};

module.exports = {
  pool,
  query,
  transaction,
  testConnection,
  closePool
};

