/**
 * [å®Œå…¨ç‰ˆ] èªè¨¼ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 */
const authenticate = async (req, res, next) => {
  try {
    const authHeader = req.headers.authorization;
    let token;

    // Header or Cookie check
    if (authHeader && authHeader.startsWith('Bearer ')) {
      token = authHeader.split(' ')[1];
    } else if (req.cookies && req.cookies.token) {
      token = req.cookies.token;
    }

    if (!token) {
      logger.warn('èªè¨¼å¤±æ•— - ãƒˆãƒ¼ã‚¯ãƒ³ãŒæä¾›ã•ã‚Œã¦ã„ã¾ã›ã‚“', {
        ip: req.ip,
        userAgent: req.get('User-Agent')
      });
      return res.status(401).json({
        success: false,
        message: 'ã‚¢ã‚¯ã‚»ã‚¹ãƒˆãƒ¼ã‚¯ãƒ³ãŒå¿…è¦ã§ã™'
      });
    }

    const user = await AuthService.getUserFromToken(token);

    if (!user) {
      logger.warn('èªè¨¼å¤±æ•— - ç„¡åŠ¹ãªãƒˆãƒ¼ã‚¯ãƒ³', {
        ip: req.ip,
        userAgent: req.get('User-Agent')
      });
      return res.status(403).json({
        success: false,
        message: 'ç„¡åŠ¹ãªãƒˆãƒ¼ã‚¯ãƒ³ã§ã™'
      });
    }

    req.user = user;

    logger.debug('èªè¨¼æˆåŠŸ', {
      userId: user.id,
      email: user.email,
      role: user.role
    });

    next();
  } catch (error) {
    next(error);
  }
};

/**
 * [å®Œå…¨ç‰ˆ] å½¹å‰²ï¼ˆãƒ­ãƒ¼ãƒ«ï¼‰ãƒ™ãƒ¼ã‚¹ã®ã‚¢ã‚¯ã‚»ã‚¹åˆ¶å¾¡ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 */
const requireRole = (roles) => {
  return (req, res, next) => {
    const requiredRoles = Array.isArray(roles) ? roles : [roles];

    if (!req.user || !req.user.role) {
      logger.warn('æ¨©é™ã‚¨ãƒ©ãƒ¼ - ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ãŒã‚ã‚Šã¾ã›ã‚“', { ip: req.ip });
      return res.status(403).json({
        success: false,
        message: 'ã‚¢ã‚¯ã‚»ã‚¹æ¨©é™ãŒã‚ã‚Šã¾ã›ã‚“'
      });
    }

    if (!requiredRoles.includes(req.user.role)) {
      logger.warn('æ¨©é™ã‚¨ãƒ©ãƒ¼ - å½¹å‰²ãŒä¸ååˆ†ã§ã™', {
        userId: req.user.id,
        userRole: req.user.role,
        requiredRoles
      });
      return res.status(403).json({
        success: false,
        message: 'ã“ã®æ“ä½œã‚’è¡Œã†æ¨©é™ãŒã‚ã‚Šã¾ã›ã‚“'
      });
    }

    next();
  };
};

/**
 * ç®¡ç†è€…æ¨©é™ãƒã‚§ãƒƒã‚¯ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ (requireRoleã‚’ä½¿ç”¨)
 */
const requireAdmin = requireRole('admin');

/**
 * ã‚¯ãƒ©ã‚¹æ‹…å½“æ•™å“¡ï¼ˆã¾ãŸã¯ç®¡ç†è€…ï¼‰æ¨©é™ãƒã‚§ãƒƒã‚¯ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 * ãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã® groupId ã¾ãŸã¯ id ã‚’ä½¿ç”¨ã—ã¦ãƒã‚§ãƒƒã‚¯
 */
const requireClassTeacher = async (req, res, next) => {
  try {
    if (!req.user) {
      return res.status(401).json({ success: false, message: 'èªè¨¼ãŒå¿…è¦ã§ã™' });
    }

    // ç®¡ç†è€…ã¯å¸¸ã«è¨±å¯
    if (req.user.role === 'admin') {
      return next();
    }

    // æ•™å“¡ã§ãªã„å ´åˆã¯æ‹’å¦
    if (req.user.role !== 'teacher') {
      return res.status(403).json({ success: false, message: 'æ¨©é™ãŒã‚ã‚Šã¾ã›ã‚“' });
    }

    // ã‚°ãƒ«ãƒ¼ãƒ—IDã®å–å¾— (params.groupId > params.id > body.groupId > body.class_id)
    const groupId = req.params.groupId || req.params.id || req.body.groupId || req.body.class_id;

    if (!groupId) {
      logger.warn('requireClassTeacher: ã‚°ãƒ«ãƒ¼ãƒ—IDãŒæŒ‡å®šã•ã‚Œã¦ã„ã¾ã›ã‚“', { url: req.originalUrl });
      return res.status(400).json({ success: false, message: 'ã‚°ãƒ«ãƒ¼ãƒ—IDãŒæŒ‡å®šã•ã‚Œã¦ã„ã¾ã›ã‚“' });
    }

    const isTeacher = await RoleService.isClassTeacher(req.user.id, groupId);

    if (!isTeacher) {
      logger.warn('æ¨©é™ã‚¨ãƒ©ãƒ¼ - æ‹…å½“æ•™å“¡ã§ã¯ã‚ã‚Šã¾ã›ã‚“', { userId: req.user.id, groupId });
      return res.status(403).json({ success: false, message: 'ã“ã®ã‚¯ãƒ©ã‚¹ã®æ‹…å½“æ•™å“¡ã§ã¯ã‚ã‚Šã¾ã›ã‚“' });
    }

    next();
  } catch (error) {
    logger.error('æ‹…å½“æ•™å“¡ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
    res.status(500).json({ success: false, message: 'ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ' });
  }
};


/**
 * ã‚ªãƒ—ã‚·ãƒ§ãƒŠãƒ«èªè¨¼ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 */
const optionalAuth = async (req, res, next) => {
  try {
    const authHeader = req.headers.authorization;
    let token = null;

    if (authHeader && authHeader.startsWith('Bearer ')) {
      token = authHeader.split(' ')[1];
    } else if (req.cookies && req.cookies.token) {
      token = req.cookies.token;
    }

    if (token) {
      const user = await AuthService.getUserFromToken(token);
      if (user) {
        req.user = user;
      }
    }

    next();
  } catch (error) {
    logger.debug('ã‚ªãƒ—ã‚·ãƒ§ãƒŠãƒ«èªè¨¼ã‚¨ãƒ©ãƒ¼:', error.message);
    next();
  }
};

/**
 * ãƒ¬ãƒ¼ãƒˆåˆ¶é™ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢ï¼ˆèªè¨¼æ¸ˆã¿ãƒ¦ãƒ¼ã‚¶ãƒ¼ç”¨ï¼‰
 */
const authRateLimit = (req, res, next) => {
  req.rateLimitKey = `auth_${req.user?.id || req.ip}`;
  next();
};

module.exports = {
  authenticate,
  requireAdmin,
  requireClassTeacher,
  optionalAuth,
  authRateLimit,
  requireRole
};

const logger = require('../utils/logger');

/**
 * [å®Œå…¨ç‰ˆ] ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒ©ãƒ¼ãƒŸãƒ‰ãƒ«ã‚¦ã‚§ã‚¢
 */
const errorHandler = (err, req, res, next) => {
  let error = { ...err };
  error.message = err.message;

  // æ§‹é€ åŒ–ã•ã‚ŒãŸã‚¨ãƒ©ãƒ¼ãƒ­ã‚°
  const errorLog = {
    message: err.message,
    stack: err.stack,
    code: err.code,
    name: err.name,
    url: req.url,
    method: req.method,
    ip: req.ip,
    userAgent: req.get('User-Agent'),
    userId: req.user?.id || null,
    timestamp: new Date().toISOString()
  };

  logger.error('ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ', errorLog);

  // MySQLã‚¨ãƒ©ãƒ¼
  if (err.code === 'ER_DUP_ENTRY') {
    const message = 'é‡è¤‡ã—ãŸãƒ‡ãƒ¼ã‚¿ãŒå­˜åœ¨ã—ã¾ã™';
    error = { message, statusCode: 400, code: 'DUPLICATE_ENTRY' };
  }
  if (err.code === 'ECONNREFUSED' || err.code === 'PROTOCOL_CONNECTION_LOST') {
    const message = 'ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šã‚¨ãƒ©ãƒ¼';
    error = { message, statusCode: 500, code: 'DB_CONNECTION_ERROR' };
  }
  if (err.code === 'ETIMEDOUT') {
    const message = 'ãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æŽ¥ç¶šãŒã‚¿ã‚¤ãƒ ã‚¢ã‚¦ãƒˆã—ã¾ã—ãŸ';
    error = { message, statusCode: 500, code: 'DB_TIMEOUT' };
  }

  // JWTã‚¨ãƒ©ãƒ¼
  if (err.name === 'JsonWebTokenError') {
    const message = 'ç„¡åŠ¹ãªãƒˆãƒ¼ã‚¯ãƒ³ã§ã™';
    error = { message, statusCode: 401, code: 'INVALID_TOKEN' };
  }
  if (err.name === 'TokenExpiredError') {
    const message = 'ãƒˆãƒ¼ã‚¯ãƒ³ã®æœ‰åŠ¹æœŸé™ãŒåˆ‡ã‚Œã¦ã„ã¾ã™';
    error = { message, statusCode: 401, code: 'TOKEN_EXPIRED' };
  }

  // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¨ãƒ©ãƒ¼
  if (err.name === 'ValidationError' || Array.isArray(err.errors)) {
    const message = err.errors?.map(e => e.msg || e.message).join(', ') || 'å…¥åŠ›ãƒ‡ãƒ¼ã‚¿ã«ã‚¨ãƒ©ãƒ¼ãŒã‚ã‚Šã¾ã™';
    error = { message, statusCode: 400, code: 'VALIDATION_ERROR', errors: err.errors };
  }

  // æ¨©é™ã‚¨ãƒ©ãƒ¼
  if (err.message && err.message.includes('æ¨©é™')) {
    error = { message: err.message, statusCode: 403, code: 'FORBIDDEN' };
  }

  // ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆã‚¨ãƒ©ãƒ¼
  const statusCode = error.statusCode || 500;
  const message = error.message || 'ã‚µãƒ¼ãƒãƒ¼ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ';
  const errorCode = error.code || 'INTERNAL_SERVER_ERROR';

  // æœ¬ç•ªç’°å¢ƒã§ã¯è©³ç´°ãªã‚¹ã‚¿ãƒƒã‚¯ãƒˆãƒ¬ãƒ¼ã‚¹ã‚’éš ã™
  const stackTrace = process.env.NODE_ENV === 'production' ? null : err.stack;

  res.status(statusCode).json({
    success: false,
    message,
    code: errorCode,
    stack: stackTrace, // é–‹ç™ºç’°å¢ƒã§ã®ã¿è¡¨ç¤º
    errors: error.errors || null // ãƒãƒªãƒ‡ãƒ¼ã‚·ãƒ§ãƒ³ã‚¨ãƒ©ãƒ¼ã®è©³ç´°
  });
};

// [é‡è¦] é–¢æ•°ã‚’ãã®ã¾ã¾ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆ
module.exports = errorHandler;

