        if (mimetype && extname) {
            return cb(null, true);
        } else {
            cb(new Error('ç”»åƒã¾ãŸã¯PDFãƒ•ã‚¡ã‚¤ãƒ«ã®ã¿ã‚¢ãƒƒãƒ—ãƒ­ãƒ¼ãƒ‰å¯èƒ½ã§ã™'));
