const logger = require('../utils/logger');

/**
 * æ¬ å¸­ç”³è«‹ç®¡ç†ã‚µãƒ¼ãƒ“ã‚¹
 */
class AbsenceRequestService {
    /**
     * æ–°ã—ã„ç”³è«‹ã‚’ä½œæˆ
     * @param {Object} requestData - ç”³è«‹ãƒ‡ãƒ¼ã‚¿
     * @returns {Promise<Object>} ä½œæˆã•ã‚ŒãŸç”³è«‹
     */
    static async createRequest(requestData) {
        try {
            const {
                studentId,
                classSessionId,
                requestType,
                requestDate,
                reason,
                attachmentUrl
            } = requestData;

            if (!studentId || !requestType || !requestDate || !reason) {
                throw new Error('å¿…é ˆãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ãŒä¸è¶³ã—ã¦ã„ã¾ã™');
            }

            const sql = `
        INSERT INTO absence_requests 
        (student_id, class_session_id, request_type, request_date, reason, attachment_url, status)
        VALUES (?, ?, ?, ?, ?, ?, 'pending')
      `;

            const result = await query(sql, [
                studentId,
                classSessionId || null,
                requestType,
                requestDate,
                reason,
                attachmentUrl || null
            ]);

            return await this.getRequest(result.insertId);
        } catch (error) {
            logger.error('ç”³è«‹ä½œæˆã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * ç”³è«‹è©³ç´°ã‚’å–å¾—
     * @param {number} requestId - ç”³è«‹ID
     * @returns {Promise<Object>} ç”³è«‹è©³ç´°
     */
    static async getRequest(requestId) {
        try {
            const sql = `
        SELECT 
          ar.id, ar.student_id, ar.class_session_id, ar.request_type,
          ar.request_date, ar.reason, ar.attachment_url, ar.status,
          ar.submitted_at, ar.updated_at,
          s.name as student_name, s.email as student_email,
          cs.class_date, cs.period_number,
          subj.subject_name
        FROM absence_requests ar
        JOIN students s ON ar.student_id = s.student_id
        LEFT JOIN class_sessions cs ON ar.class_session_id = cs.id
        LEFT JOIN subjects subj ON cs.subject_id = subj.id
        WHERE ar.id = ?
      `;

            const results = await query(sql, [requestId]);

            if (results.length === 0) {
                throw new Error('ç”³è«‹ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
            }

            return results[0];
        } catch (error) {
            logger.error('ç”³è«‹è©³ç´°å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * å­¦ç”Ÿã®ç”³è«‹ä¸€è¦§ã‚’å–å¾—
     * @param {string} studentId - å­¦ç”ŸID
     * @param {Object} options - æ¤œç´¢ã‚ªãƒ—ã‚·ãƒ§ãƒ³
     * @returns {Promise<Array>} ç”³è«‹ä¸€è¦§
     */
    static async getRequestsByStudent(studentId, options = {}) {
        try {
            const { status, startDate, endDate, limit = 50, offset = 0 } = options;

            let sql = `
        SELECT 
          ar.id, ar.student_id, ar.class_session_id, ar.request_type,
          ar.request_date, ar.reason, ar.attachment_url, ar.status,
          ar.submitted_at, ar.updated_at,
          cs.class_date, cs.period_number,
          subj.subject_name
        FROM absence_requests ar
        LEFT JOIN class_sessions cs ON ar.class_session_id = cs.id
        LEFT JOIN subjects subj ON cs.subject_id = subj.id
        WHERE ar.student_id = ?
      `;

            const params = [studentId];

            if (status) {
                sql += ' AND ar.status = ?';
                params.push(status);
            }

            if (startDate) {
                sql += ' AND ar.request_date >= ?';
                params.push(startDate);
            }

            if (endDate) {
                sql += ' AND ar.request_date <= ?';
                params.push(endDate);
            }

            sql += ' ORDER BY ar.submitted_at DESC LIMIT ? OFFSET ?';
            params.push(limit, offset);

            return await query(sql, params);
        } catch (error) {
            logger.error('å­¦ç”Ÿç”³è«‹ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * æ•™å“¡ã®æ‰¿èªå¾…ã¡ç”³è«‹ä¸€è¦§ã‚’å–å¾—
     * @param {number} teacherId - æ•™å“¡ID
     * @param {Object} options - æ¤œç´¢ã‚ªãƒ—ã‚·ãƒ§ãƒ³
     * @returns {Promise<Array>} ç”³è«‹ä¸€è¦§
     */
    static async getPendingRequestsForTeacher(teacherId, options = {}) {
        try {
            // æ•™å“¡ãŒæ‹…å½“ã™ã‚‹ã‚°ãƒ«ãƒ¼ãƒ—ã®å­¦ç”Ÿã®ç”³è«‹ã‚’å–å¾—
            const { limit = 50, offset = 0 } = options;

            const sql = `
        SELECT DISTINCT
          ar.id, ar.student_id, ar.class_session_id, ar.request_type,
          ar.request_date, ar.reason, ar.attachment_url, ar.status,
          ar.submitted_at, ar.updated_at,
          s.name as student_name, s.email as student_email,
          g.name as group_name,
          cs.class_date, cs.period_number,
          subj.subject_name
        FROM absence_requests ar
        JOIN students s ON ar.student_id = s.student_id
        JOIN group_members gm ON s.student_id = gm.student_id
