    static async _updateAttendanceStatus(conn, requestData) {
        try {
            const { student_id, class_session_id, request_type, request_date } = requestData;

            // request_typeã«å¿œã˜ãŸå‡ºæ¬ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚’æ±ºå®š
            let newStatus;
            switch (request_type) {
