const { query } = require('../config/database');
const logger = require('../utils/logger');

/**
 * AttendanceService æ‹¡å¼µãƒ¡ã‚½ãƒƒãƒ‰
 * æ™‚åˆ»ãƒ™ãƒ¼ã‚¹å‡ºæ¬ åˆ¤å®šã¨ QRé€£æºã®æ©Ÿèƒ½ã‚’æä¾›
 */
class AttendanceServiceExtension {
    /**
     * æ™‚åˆ»ãƒ™ãƒ¼ã‚¹ã®å‡ºæ¬ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹åˆ¤å®š
     * @param {Date} scanTime - ã‚¹ã‚­ãƒ£ãƒ³æ™‚åˆ»
     * @param {string} classStartTime - æŽˆæ¥­é–‹å§‹æ™‚åˆ» (HH:MM:SSå½¢å¼)
     * @param {string} classDate - æŽˆæ¥­æ—¥ (YYYY-MM-DDå½¢å¼)
     * @returns {Promise<string>} å‡ºæ¬ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ (present/late/absent)
     */
    static async determineAttendanceStatus(scanTime, classStartTime, classDate) {
        try {
            // ç³»çµ±è¨­å®šã‹ã‚‰é…åˆ»é–‰åˆ‡åˆ†æ•°ã‚’å–å¾—ï¼ˆãƒ‡ãƒ•ã‚©ãƒ«ãƒˆ15åˆ†ï¼‰
            let lateThresholdMinutes = 15;

            try {
                const settingsSql = `
          SELECT setting_value 
          FROM system_settings 
          WHERE setting_key = 'late_threshold_minutes'
        `;
                const settings = await query(settingsSql);
                if (settings.length > 0) {
                    lateThresholdMinutes = parseInt(settings[0].setting_value) || 15;
                }
            } catch (error) {
                logger.warn('é…åˆ»è¨­å®šã®å–å¾—ã«å¤±æ•—ã€ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆå€¤ã‚’ä½¿ç”¨:', error.message);
            }

            // æŽˆæ¥­é–‹å§‹æ™‚åˆ»ã‚’Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã«å¤‰æ›
            const [hours, minutes, seconds] = classStartTime.split(':').map(Number);
            const classStart = new Date(classDate);
            classStart.setHours(hours, minutes, seconds || 0, 0);

            // ã‚¹ã‚­ãƒ£ãƒ³æ™‚åˆ»ã¨é–‹å§‹æ™‚åˆ»ã®å·®åˆ†ã‚’è¨ˆç®—ï¼ˆåˆ†å˜ä½ï¼‰
            const diffMs = scanTime - classStart;
            const diffMinutes = Math.floor(diffMs / (1000 * 60));

            // ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹åˆ¤å®š
            if (diffMinutes < 0) {
                // æŽˆæ¥­é–‹å§‹å‰ã®ã‚¹ã‚­ãƒ£ãƒ³ = å‡ºå¸­
                return 'present';
            } else if (diffMinutes <= lateThresholdMinutes) {
                // é–‹å§‹å¾Œã€é…åˆ»é–‰åˆ‡åˆ†æ•°ä»¥å†… = å‡ºå¸­
                return 'present';
            } else {
                // é…åˆ»é–‰åˆ‡åˆ†æ•°ã‚’è¶…ãˆã¦ã„ã‚‹ = é…åˆ»
                return 'late';
            }
        } catch (error) {
            logger.error('å‡ºæ¬ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹åˆ¤å®šã‚¨ãƒ©ãƒ¼:', error);
            // ã‚¨ãƒ©ãƒ¼æ™‚ã¯å®‰å…¨å´ã«å€’ã—ã¦é…åˆ»ã¨ã™ã‚‹
            return 'late';
        }
    }

    /**
     * æ™‚é–“å‰²ã¨ã®ç…§åˆã§ç¾åœ¨ã®æŽˆæ¥­ã‚’å–å¾—
     * @param {string} studentId - å­¦ç”ŸID
     * @param {Date} scanTime - ã‚¹ã‚­ãƒ£ãƒ³æ™‚åˆ»
     * @returns {Promise<Object|null>} è©²å½“ã™ã‚‹æŽˆæ¥­ã‚»ãƒƒã‚·ãƒ§ãƒ³
     */
    static async findCurrentClass(studentId, scanTime = new Date()) {
        try {
            const currentDate = scanTime.toISOString().split('T')[0];
            const currentTime = scanTime.toTimeString().split(' ')[0];

            const sql = `
        SELECT 
          cs.id, cs.subject_id, cs class_date, cs.start_time, cs.end_time,
          s.subject_name, cs.teacher_name, cs.room,
          t.group_id, g.name as group_name
        FROM class_sessions cs
        JOIN subjects s ON cs.subject_id = s.id
        JOIN timetables t ON cs.timetable_id = t.id
        JOIN groups g ON t.group_id = g.id
        JOIN group_members gm ON t.group_id = gm.group_id
        WHERE gm.student_id = ?
          AND cs.class_date = ?
          AND cs.start_time <= ?
          AND cs.end_time >= ?
          AND cs.is_cancelled = FALSE
          AND gm.status = 'active'
        ORDER BY cs.start_time ASC
        LIMIT 1
      `;

            const results = await query(sql, [studentId, currentDate, currentTime, currentTime]);

            if (results.length > 0) {
                return results[0];
            }

            return null;
        } catch (error) {
            logger.error('ç¾åœ¨ã®æŽˆæ¥­æ¤œç´¢ã‚¨ãƒ©ãƒ¼:', error);
            return null;
        }
    }

    /**
     * QRã‚¹ã‚­ãƒ£ãƒ³ã«ã‚ˆã‚‹è‡ªå‹•å‡ºå¸­è¨˜éŒ²ï¼ˆçµ±åˆåž‹ã‚·ã‚¹ãƒ†ãƒ ç”¨ï¼‰
     * @param {string} studentId - å­¦ç”ŸID
     * @param {string} qrCode - QRã‚³ãƒ¼ãƒ‰
     * @param {string} ipAddress - IPã‚¢ãƒ‰ãƒ¬ã‚¹
     * @param {string} userAgent - ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚¨ãƒ¼ã‚¸ã‚§ãƒ³ãƒˆ
     * @returns {Promise<Object>} è¨˜éŒ²çµæžœ
     */
    static async recordAttendanceByQR(studentId, qrCode, ipAddress, userAgent) {
        try {
            const QRService = require('./QRService');

            // QRServiceã‚’ä½¿ç”¨ã—ã¦ã‚¹ã‚­ãƒ£ãƒ³å‡¦ç†
            const result = await QRService.scanQRCodeWithIPValidation(
                { qrCode, studentId },
                ipAddress,
                userAgent
            );

            return result;
        } catch (error) {
            logger.error('QRã‚¹ã‚­ãƒ£ãƒ³ã«ã‚ˆã‚‹å‡ºå¸­è¨˜éŒ²ã‚¨ãƒ©ãƒ¼:', error);
            return {
                success: false,
                message: 'å‡ºå¸­è¨˜éŒ²ã«å¤±æ•—ã—ã¾ã—ãŸ',
                error: error.message
            };
        }
    }
}

