    getClasses: async (search, subjectId, isActive) => {
        let sql = `SELECT c.*, s.subject_name, s.subject_code 
                   FROM classes c 
                   JOIN subjects s ON c.subject_id = s.id`;
        const params = [];
        const conditions = [];

        try {
            if (search) {
