                    updateFields.push(`${field} = ?`);
                    updateParams.push(updateData[field]);
                }
            }

            if (updateFields.length === 0) {
                return { success: false, message: 'æ›´æ–°ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“' };
            }

            updateFields.push('updated_at = NOW()');
            updateParams.push(classId);

            const sql = `UPDATE classes SET ${updateFields.join(', ')} WHERE id = ?`;
            await connection.execute(sql, updateParams);

            return { success: true, message: 'æŽˆæ¥­æƒ…å ±ãŒæ›´æ–°ã•ã‚Œã¾ã—ãŸ' };

        } catch (error) {
            console.error("ClassService.updateClass ã‚¨ãƒ©ãƒ¼:", error.message);
            return { success: false, message: 'æŽˆæ¥­æƒ…å ±ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ' };
        } finally {
            if (connection) connection.release();
        }
    },

    /**
     * æŽˆæ¥­ã®å‰Šé™¤
     * @param {string|number} classId - æŽˆæ¥­ID
     */
    deleteClass: async (classId) => {
