            }

            // æŽˆæ¥­ã®å‰Šé™¤
            await connection.execute("DELETE FROM classes WHERE id = ?", [classId]);

            return { success: true, message: 'æŽˆæ¥­ãŒå‰Šé™¤ã•ã‚Œã¾ã—ãŸ' };

        } catch (error) {
            console.error("ClassService.deleteClass ã‚¨ãƒ©ãƒ¼:", error.message);
            return { success: false, message: 'æŽˆæ¥­ã®å‰Šé™¤ã«å¤±æ•—ã—ã¾ã—ãŸ' };
        } finally {
            if (connection) connection.release();
        }
    }
};

