  /**
   * ã‚¤ãƒ™ãƒ³ãƒˆä¸€è¦§ã®å–å¾—
   */
  static async getEvents(options = {}) {
    try {
      const {
        userId,
        startDate,
        endDate,
        isPublic,
        limit,
        offset = 0
      } = options;

      let sql = `
        SELECT 
          e.id,
          e.title,
          e.description,
          e.start_date,
          e.end_date,
          e.location,
          e.created_by,
          e.is_public,
          e.created_at,
          e.updated_at,
          u.name as creator_name,
          ep.status as participation_status
        FROM events e
        LEFT JOIN users u ON e.created_by = u.id
        LEFT JOIN event_participants ep ON e.id = ep.event_id AND ep.user_id = ?
        WHERE 1=1
      `;
      const params = [userId || null];

      if (startDate) {
        sql += ' AND e.start_date >= ?';
        params.push(startDate);
      }

      if (endDate) {
        sql += ' AND e.end_date <= ?';
        params.push(endDate);
      }

      if (isPublic !== undefined) {
        sql += ' AND e.is_public = ?';
        params.push(Boolean(isPublic));
      }

      // ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒå‚åŠ ã—ã¦ã„ã‚‹ã‚¤ãƒ™ãƒ³ãƒˆã€ã¾ãŸã¯å…¬é–‹ã‚¤ãƒ™ãƒ³ãƒˆã€ã¾ãŸã¯ä½œæˆã—ãŸã‚¤ãƒ™ãƒ³ãƒˆã®ã¿è¡¨ç¤º
      if (userId) {
        sql += ` AND (
          ep.user_id = ? OR 
          e.is_public = TRUE OR 
          e.created_by = ?
        )`;
        params.push(userId, userId);
      } else {
        // æœªãƒ­ã‚°ã‚¤ãƒ³ã®å ´åˆã¯å…¬é–‹ã‚¤ãƒ™ãƒ³ãƒˆã®ã¿
        sql += ' AND e.is_public = TRUE';
      }

      sql += ' ORDER BY e.start_date ASC';

      if (limit) {
        sql += ' LIMIT ?';
        params.push(parseInt(limit));
      }

      if (offset) {
        sql += ' OFFSET ?';
        params.push(parseInt(offset));
      }

      const events = await query(sql, params);

      // å‚åŠ è€…æ•°ã‚’å–å¾—
      for (const event of events) {
        const participants = await query(
          'SELECT COUNT(*) as count FROM event_participants WHERE event_id = ? AND status = ?',
          [event.id, 'accepted']
        );
        event.participant_count = participants[0]?.count || 0;
      }

      // ç·æ•°ã‚’å–å¾—
      let countSql = `
        SELECT COUNT(*) as total
        FROM events e
        LEFT JOIN event_participants ep ON e.id = ep.event_id AND ep.user_id = ?
        WHERE 1=1
      `;
      const countParams = [userId || null];

      if (startDate) {
        countSql += ' AND e.start_date >= ?';
        countParams.push(startDate);
      }

      if (endDate) {
        countSql += ' AND e.end_date <= ?';
        countParams.push(endDate);
      }

      if (isPublic !== undefined) {
        countSql += ' AND e.is_public = ?';
        countParams.push(Boolean(isPublic));
      }

      if (userId) {
        countSql += ` AND (
          ep.user_id = ? OR 
          e.is_public = TRUE OR 
          e.created_by = ?
        )`;
        countParams.push(userId, userId);
      } else {
        countSql += ' AND e.is_public = TRUE';
      }

      const countResult = await query(countSql, countParams);
      const total = countResult[0]?.total || 0;

      return {
        success: true,
        data: {
          events,
          total,
          limit: limit ? parseInt(limit) : null,
          offset: parseInt(offset)
        }
      };
    } catch (error) {
      logger.error('ã‚¤ãƒ™ãƒ³ãƒˆä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: 'ã‚¤ãƒ™ãƒ³ãƒˆä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

