class GroupService {
  /**
   * [å®Œå…¨ç‰ˆ] ã‚°ãƒ«ãƒ¼ãƒ—ã®ä½œæˆ (ä½œæˆè€…IDã‚‚ä¿å­˜)
   */
  static async createGroup(data, creatorId) {
    try {
      if (!creatorId) {
        throw new Error('ä½œæˆè€…ID (creatorId) ãŒå¿…è¦ã§ã™');
      }

      const result = await transaction(async (conn) => {
        const existingGroups = await query(
          'SELECT id FROM `groups` WHERE name = ?',
          [data.name]
        );

        if (existingGroups.length > 0) {
          throw new Error('ã“ã®ã‚°ãƒ«ãƒ¼ãƒ—åã¯æ—¢ã«ä½¿ç”¨ã•ã‚Œã¦ã„ã¾ã™');
        }

        const insertResult = await query(
          'INSERT INTO `groups` (name, description, created_by, is_active) VALUES (?, ?, ?, ?)',
          [
            data.name,
            data.description || null,
            creatorId, // [ä¿®æ­£] ä½œæˆè€…IDã‚’ã‚»ãƒƒãƒˆ
            data.is_active !== undefined ? data.is_active : true
          ]
        );

        return {
          success: true,
          message: 'ã‚°ãƒ«ãƒ¼ãƒ—ãŒä½œæˆã•ã‚Œã¾ã—ãŸ',
          data: { id: insertResult.insertId }
        };
      });

      logger.info('ã‚°ãƒ«ãƒ¼ãƒ—ä½œæˆæˆåŠŸ', { groupId: result.data.id, name: data.name, creatorId });
      return result;
    } catch (error) {
      logger.error('ã‚°ãƒ«ãƒ¼ãƒ—ä½œæˆã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: error.message || 'ã‚°ãƒ«ãƒ¼ãƒ—ã®ä½œæˆã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * ã‚°ãƒ«ãƒ¼ãƒ—ä¸€è¦§ã®å–å¾—
   */
  static async getGroups(options = {}) {
    try {
      const {
        search, // [ä¿®æ­£] 'search' ãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã‚‚å—ã‘å–ã‚‹
        is_active,
        created_by,
        student_id,
        include_members,
        limit,
        offset = 0
      } = options;

      let sql = `
        SELECT 
          g.id, g.name, g.is_active, g.created_at, g.created_by,
          u.name as creator_name,
          (SELECT COUNT(*) FROM group_members gm WHERE gm.group_id = g.id) as member_count
        FROM \`groups\` g
        LEFT JOIN users u ON g.created_by = u.id
      `;
      const params = [];

      if (student_id) {
        sql += ` JOIN group_members gm_filter ON g.id = gm_filter.group_id WHERE gm_filter.student_id = ?`;
        params.push(student_id);
      } else if (options.teacher_id) { // [è¿½åŠ ] æ‹…å½“æ•™å“¡ã§ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
        sql += ` JOIN group_teachers gt_filter ON g.id = gt_filter.group_id WHERE gt_filter.user_id = ?`;
        params.push(options.teacher_id);
      } else {
        sql += ` WHERE 1=1`;
      }

      if (search) {
        sql += ' AND g.name LIKE ?';
        params.push(`%${search}%`);
      }
      if (is_active !== undefined) {
        sql += ' AND g.is_active = ?';
        params.push(Boolean(is_active));
      }
      if (created_by) {
        sql += ' AND g.created_by = ?';
        params.push(created_by);
      }

      sql += ' ORDER BY g.created_at DESC';

      if (limit) {
        sql += ' LIMIT ?';
        params.push(parseInt(limit));
      }
      if (offset) {
        sql += ' OFFSET ?';
        params.push(parseInt(offset));
      }

      // ãƒ‡ãƒãƒƒã‚°ãƒ­ã‚°
      logger.debug('ã‚°ãƒ«ãƒ¼ãƒ—å–å¾—SQL:', { sql, params, options });

      let groups = await query(sql, params);

      if (include_members && groups.length > 0) {
        for (const group of groups) {
          const members = await query(
            `SELECT s.student_id, s.name, gm.status, gm.joined_at 
              FROM group_members gm
              JOIN students s ON gm.student_id = s.student_id
              WHERE gm.group_id = ?`,
            [group.id]
          );
          group.members = members;
        }
      }

      return {
        success: true,
        data: {
          groups,
          total: groups.length
        }
      };
    } catch (error) {
      logger.error('ã‚°ãƒ«ãƒ¼ãƒ—ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: 'ã‚°ãƒ«ãƒ¼ãƒ—ä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * ç‰¹å®šã‚°ãƒ«ãƒ¼ãƒ—ã®å–å¾—
   */
  static async getGroup(id) {
    try {
      const groups = await query(
