   */
  static async getNotifications(options = {}) {
    try {
      const {
        user_id,
        student_id,
        type,
        priority,
        is_read,
        limit,
        offset = 0
      } = options;

      let sql = `
        SELECT id, title, message, type, priority, is_read, read_at, created_at
        FROM notifications
        WHERE 1=1
      `;
      const params = [];
      const countParams = [];

      if (user_id) {
        sql += ' AND user_id = ?';
        params.push(user_id);
      }
      if (student_id) {
        sql += ' AND student_id = ?';
        params.push(student_id);
      }

      if (type) {
        sql += ' AND type = ?';
        params.push(type);
      }
      if (priority) {
        sql += ' AND priority = ?';
        params.push(priority);
      }
      if (is_read !== undefined) {
        sql += ' AND is_read = ?';
        params.push(Boolean(is_read));
      }

      const countSql = `SELECT COUNT(*) as total FROM (${sql}) as count_query`;
      Object.assign(countParams, params);

      sql += ' ORDER BY created_at DESC';

      if (limit) {
        sql += ' LIMIT ?';
        params.push(parseInt(limit));
      }
      if (offset) {
        sql += ' OFFSET ?';
        params.push(parseInt(offset));
      }

      const notifications = await query(sql, params);
      const countResult = await query(countSql, countParams);
      const total = countResult[0].total;

      return {
        success: true,
        data: {
          notifications,
          total,
          limit: limit ? parseInt(limit) : null,
          offset: parseInt(offset)
        }
      };
    } catch (error) {
      logger.error('é€šçŸ¥ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: 'é€šçŸ¥ä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

