        SELECT 
          id, name, type, address, phone, email, 
          created_at, updated_at
        FROM organizations
        WHERE id = ?
      `;

            const results = await query(sql, [orgId]);

            if (results.length === 0) {
                throw new Error('çµ„ç¹”ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
            }

            return results[0];
        } catch (error) {
            logger.error('çµ„ç¹”æƒ…å ±å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * ã™ã¹ã¦ã®çµ„ç¹”ä¸€è¦§ã‚’å–å¾—
     * @returns {Promise<Array>} çµ„ç¹”ä¸€è¦§
     */
    static async getAllOrganizations() {
        try {
            const sql = `
        SELECT 
          id, name, type, address, phone, email, 
          created_at, updated_at
        FROM organizations
        ORDER BY created_at DESC
      `;

            return await query(sql);
        } catch (error) {
            logger.error('çµ„ç¹”ä¸€è¦§å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * çµ„ç¹”æƒ…å ±ã‚’æ›´æ–°ï¼ˆç®¡ç†è€…ã®ã¿ï¼‰
     * @param {number} orgId - çµ„ç¹”ID
     * @param {Object} data - æ›´æ–°ãƒ‡ãƒ¼ã‚¿
     * @returns {Promise<Object>} æ›´æ–°ã•ã‚ŒãŸçµ„ç¹”æƒ…å ±
     */
    static async updateOrganization(orgId, data) {
        try {
            const { name, type, address, phone, email } = data;

            // æ›´æ–°å¯èƒ½ãªãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã®ã¿ã‚’æŠ½å‡º
            const updateFields = [];
            const updateValues = [];

            if (name !== undefined) {
                updateFields.push('name = ?');
                updateValues.push(name);
            }
            if (type !== undefined) {
                updateFields.push('type = ?');
                updateValues.push(type);
            }
            if (address !== undefined) {
                updateFields.push('address = ?');
                updateValues.push(address);
            }
            if (phone !== undefined) {
                updateFields.push('phone = ?');
                updateValues.push(phone);
            }
            if (email !== undefined) {
                updateFields.push('email = ?');
                updateValues.push(email);
            }

            if (updateFields.length === 0) {
                throw new Error('æ›´æ–°ã™ã‚‹ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ãŒã‚ã‚Šã¾ã›ã‚“');
            }

            updateValues.push(orgId);

            const sql = `
        UPDATE organizations
        SET ${updateFields.join(', ')}
        WHERE id = ?
      `;

            await query(sql, updateValues);

            // æ›´æ–°å¾Œã®ãƒ‡ãƒ¼ã‚¿ã‚’å–å¾—
            return await this.getOrganization(orgId);
        } catch (error) {
            logger.error('çµ„ç¹”æƒ…å ±æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * æ–°ã—ã„çµ„ç¹”ã‚’ä½œæˆ
     * @param {Object} data - çµ„ç¹”ãƒ‡ãƒ¼ã‚¿
     * @returns {Promise<Object>} ä½œæˆã•ã‚ŒãŸçµ„ç¹”æƒ…å ±
     */
    static async createOrganization(data) {
        try {
            const { name, type, address, phone, email } = data;

            if (!name || !type) {
                throw new Error('çµ„ç¹”åã¨ç¨®åˆ¥ã¯å¿…é ˆã§ã™');
            }

            const sql = `
        INSERT INTO organizations (name, type, address, phone, email)
        VALUES (?, ?, ?, ?, ?)
      `;

            const result = await query(sql, [name, type, address, phone, email]);

            return await this.getOrganization(result.insertId);
        } catch (error) {
            logger.error('çµ„ç¹”ä½œæˆã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * çµ„ç¹”ã‚’å‰Šé™¤ï¼ˆç®¡ç†è€…ã®ã¿ï¼‰
     * @param {number} orgId - çµ„ç¹”ID
     * @returns {Promise<boolean>} å‰Šé™¤æˆåŠŸãƒ•ãƒ©ã‚°
     */
    static async deleteOrganization(orgId) {
        try {
            const sql = 'DELETE FROM organizations WHERE id = ?';
            await query(sql, [orgId]);

            return true;
        } catch (error) {
            logger.error('çµ„ç¹”å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }

    /**
     * çµ„ç¹”ã®çµ±è¨ˆæƒ…å ±ã‚’å–å¾—
     * @param {number} orgId - çµ„ç¹”ID
     * @returns {Promise<Object>} çµ±è¨ˆæƒ…å ±
     */
    static async getOrganizationStats(orgId) {
        try {
            // ã‚°ãƒ«ãƒ¼ãƒ—æ•°ã‚’å–å¾—
            const groupCountSql = `
        SELECT COUNT(*) as count
        FROM groups
        WHERE organization_id = ? AND is_active = TRUE
      `;
            const groupCount = await query(groupCountSql, [orgId]);

            // å­¦ç”Ÿæ•°ã‚’å–å¾—ï¼ˆã‚°ãƒ«ãƒ¼ãƒ—ãƒ¡ãƒ³ãƒãƒ¼çµŒç”±ï¼‰
            const studentCountSql = `
        SELECT COUNT(DISTINCT gm.student_id) as count
        FROM group_members gm
        JOIN groups g ON gm.group_id = g.id
        WHERE g.organization_id = ? AND gm.status = 'active'
      `;
            const studentCount = await query(studentCountSql, [orgId]);

            // æ•™å“¡æ•°ã‚’å–å¾—ï¼ˆã‚°ãƒ«ãƒ¼ãƒ—æ‹…å½“æ•™å“¡çµŒç”±ï¼‰
            const teacherCountSql = `
        SELECT COUNT(DISTINCT gt.user_id) as count
        FROM group_teachers gt
        JOIN groups g ON gt.group_id = g.id
        WHERE g.organization_id = ?
      `;
            const teacherCount = await query(teacherCountSql, [orgId]);

            return {
                totalGroups: groupCount[0].count,
                totalStudents: studentCount[0].count,
                totalTeachers: teacherCount[0].count
            };
        } catch (error) {
            logger.error('çµ„ç¹”çµ±è¨ˆæƒ…å ±å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            throw error;
        }
    }
}

