  static async verifyQRCode(qrData) {
    try {
      if (!qrData) {
        return {
          success: false,
          message: 'QRã‚³ãƒ¼ãƒ‰ãƒ‡ãƒ¼ã‚¿ãŒæä¾›ã•ã‚Œã¦ã„ã¾ã›ã‚“'
