  /**
   * QRã‚³ãƒ¼ãƒ‰ã‚¹ã‚­ãƒ£ãƒ³ï¼ˆIPæ¤œè¨¼ä»˜ãã€æ–°ã‚·ã‚¹ãƒ†ãƒ ç”¨ï¼‰
   * @param {Object} data - ã‚¹ã‚­ãƒ£ãƒ³ãƒ‡ãƒ¼ã‚¿
   * @param {string} ipAddress - ã‚¹ã‚­ãƒ£ãƒ³å…ƒIPã‚¢ãƒ‰ãƒ¬ã‚¹
   * @param {string} userAgent - ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚¨ãƒ¼ã‚¸ã‚§ãƒ³ãƒˆ
   * @returns {Promise<Object>} ã‚¹ã‚­ãƒ£ãƒ³çµæžœ
   */
  static async scanQRCodeWithIPValidation(data, ipAddress, userAgent) {
    try {
      const { qrCode, studentId } = data;
      const scanTime = new Date();

      // 1. IPã‚¢ãƒ‰ãƒ¬ã‚¹æ¤œè¨¼
      const ipCheck = await SecurityService.isIPAllowed(ipAddress);

      if (!ipCheck.allowed) {
        // IPãŒè¨±å¯ã•ã‚Œã¦ã„ãªã„å ´åˆã€ãƒ­ã‚°ã«è¨˜éŒ²ã—ã¦æ‹’å¦
        await SecurityService.logScan({
          qrCodeId: null,
          studentId,
          ipAddress,
          isAllowed: false,
          userAgent,
          result: 'ip_denied',
          errorMessage: 'è¨±å¯ã•ã‚Œã¦ã„ãªã„ãƒãƒƒãƒˆãƒ¯ãƒ¼ã‚¯ã‹ã‚‰ã®ã‚¢ã‚¯ã‚»ã‚¹ã§ã™'
        });

        return {
          success: false,
          message: 'æŒ‡å®šã®Wi-Fiãƒãƒƒãƒˆãƒ¯ãƒ¼ã‚¯ã«æŽ¥ç¶šã—ã¦ãã ã•ã„',
          error: 'IP_NOT_ALLOWED'
        };
      }

      // 2. QRã‚³ãƒ¼ãƒ‰ã®æ¤œè¨¼
      const qrCodeData = await query(
        'SELECT * FROM qr_codes WHERE code = ? AND is_active = TRUE',
        [qrCode]
      );

      if (qrCodeData.length === 0) {
        await SecurityService.logScan({
          qrCodeId: null,
          studentId,
          ipAddress,
          isAllowed: true,
          userAgent,
          result: 'invalid_qr',
          errorMessage: 'ç„¡åŠ¹ãªQRã‚³ãƒ¼ãƒ‰ã§ã™'
        });

        return {
          success: false,
          message: 'ç„¡åŠ¹ãªQRã‚³ãƒ¼ãƒ‰ã§ã™'
        };
      }

      const qrCodeInfo = qrCodeData[0];

      // æœ‰åŠ¹æœŸé™ãƒã‚§ãƒƒã‚¯
      if (qrCodeInfo.expires_at && new Date(qrCodeInfo.expires_at) < scanTime) {
        await SecurityService.logScan({
          qrCodeId: qrCodeInfo.id,
          studentId,
          ipAddress,
          isAllowed: true,
          userAgent,
          result: 'error',
          errorMessage: 'QRã‚³ãƒ¼ãƒ‰ã®æœ‰åŠ¹æœŸé™ãŒåˆ‡ã‚Œã¦ã„ã¾ã™'
        });

        return {
          success: false,
          message: 'QRã‚³ãƒ¼ãƒ‰ã®æœ‰åŠ¹æœŸé™ãŒåˆ‡ã‚Œã¦ã„ã¾ã™'
        };
      }

      // 3. å­¦ç”Ÿã®ç¾åœ¨ã®æŽˆæ¥­ã‚»ãƒƒã‚·ãƒ§ãƒ³ã‚’æ¤œç´¢ï¼ˆæ–°ã—ã„class_sessionsãƒ†ãƒ¼ãƒ–ãƒ«ã‚’ä½¿ç”¨ï¼‰
      const currentDate = scanTime.toISOString().split('T')[0];
      const currentTime = scanTime.toTimeString().split(' ')[0];

      const sessionSql = `
        SELECT 
          cs.id, cs.subject_id, cs.class_date, cs.start_time, cs.end_time,
