          qrCodeId: qrCodeInfo.id,
          studentId,
          ipAddress,
          isAllowed: true,
          userAgent,
          result: 'error',
          errorMessage: 'ç¾åœ¨æ™‚åˆ»ã«è©²å½“ã™ã‚‹æŽˆæ¥­ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“'
        });

        return {
          success: false,
          message: 'ç¾åœ¨æ™‚åˆ»ã«è©²å½“ã™ã‚‹æŽˆæ¥­ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“'
        };
      }

      const session = sessions[0];

      // 4. å‡ºæ¬ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚’åˆ¤å®šï¼ˆæ™‚åˆ»ãƒ™ãƒ¼ã‚¹ï¼‰
