        end_date,
        user_id, 
        student_id, 
        class_id
      } = options;

      let sql = '';
      const params = [];

      if (student_id || class_id) {
        // --- å­¦ç”Ÿãƒ¬ãƒãƒ¼ãƒˆ (detailed_attendance_records) ---
        sql = `
          SELECT 
            dar.id,
            dar.attendance_date as date,
            dar.status,
            dar.check_in_time,
            dar.check_out_time,
            dar.notes,
            s.name as student_name,
            s.student_id,
            c.class_code,
            sj.subject_name
          FROM detailed_attendance_records dar
          JOIN students s ON dar.student_id = s.student_id
          JOIN classes c ON dar.class_id = c.id
          JOIN subjects sj ON c.subject_id = sj.id
          WHERE dar.attendance_date >= ? AND dar.attendance_date <= ?
        `;
        params.push(start_date, end_date);

        if (student_id) {
          sql += ' AND dar.student_id = ?';
          params.push(student_id);
        }
        if (class_id) {
          sql += ' AND dar.class_id = ?';
          params.push(class_id);
        }
        sql += ' ORDER BY dar.attendance_date DESC, s.name ASC';

      } else {
        // --- å¾“æ¥­å“¡ãƒ¬ãƒãƒ¼ãƒˆ (user_attendance_records) ---
        sql = `
          SELECT 
            uar.id,
            uar.date,
            uar.status,
            uar.check_in_time,
            uar.check_out_time,
            uar.reason,
            u.name as user_name,
            u.employee_id
