class RoleService {
    /**
     * ç®¡ç†è€…ã‹ã©ã†ã‹ãƒã‚§ãƒƒã‚¯
     * @param {Object} user - ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
     * @returns {boolean}
     */
    static isAdmin(user) {
        return user && user.role === 'admin';
    }

    /**
     * æ•™å“¡ã‹ã©ã†ã‹ãƒã‚§ãƒƒã‚¯
     * @param {Object} user - ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
     * @returns {boolean}
     */
    static isTeacher(user) {
        return user && (user.role === 'teacher' || user.role === 'admin');
    }

    /**
     * æŒ‡å®šã‚°ãƒ«ãƒ¼ãƒ—ã®æ‹…å½“æ•™å“¡ã‹ã©ã†ã‹ãƒã‚§ãƒƒã‚¯
     * @param {number} userId - ãƒ¦ãƒ¼ã‚¶ãƒ¼ID
     * @param {number} groupId - ã‚°ãƒ«ãƒ¼ãƒ—ID
     * @returns {Promise<boolean>}
     */
    static async isClassTeacher(userId, groupId) {
        try {
            // ç®¡ç†è€…ã¯å¸¸ã«ã‚¢ã‚¯ã‚»ã‚¹å¯èƒ½
            const users = await query('SELECT role FROM users WHERE id = ?', [userId]);
            if (users.length > 0 && users[0].role === 'admin') {
                return true;
            }

            const result = await query(
                'SELECT id FROM group_teachers WHERE group_id = ? AND user_id = ?',
                [groupId, userId]
            );
            return result.length > 0;
        } catch (error) {
            logger.error('æ‹…å½“æ•™å“¡ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
            return false;
        }
    }

    /**
     * ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒã‚¢ã‚¯ã‚»ã‚¹å¯èƒ½ãªã‚°ãƒ«ãƒ¼ãƒ—IDãƒªã‚¹ãƒˆã‚’å–å¾—
     * @param {number} userId - ãƒ¦ãƒ¼ã‚¶ãƒ¼ID
     * @param {string} role - ãƒ¦ãƒ¼ã‚¶ãƒ¼ãƒ­ãƒ¼ãƒ«
     * @returns {Promise<Array<number>>} ã‚°ãƒ«ãƒ¼ãƒ—IDã®é…åˆ—
     */
    static async getAccessibleGroups(userId, role) {
        try {
            if (role === 'admin') {
                // ç®¡ç†è€…ã¯å…¨ã‚°ãƒ«ãƒ¼ãƒ—ã«ã‚¢ã‚¯ã‚»ã‚¹å¯èƒ½
                const groups = await query('SELECT id FROM `groups`');
                return groups.map(g => g.id);
            } else if (role === 'teacher') {
                // æ‹…å½“ã—ã¦ã„ã‚‹ã‚°ãƒ«ãƒ¼ãƒ—ã®ã¿
                const groups = await query(
                    'SELECT group_id FROM group_teachers WHERE user_id = ?',
                    [userId]
                );
                return groups.map(g => g.group_id);
            } else {
                // å­¦ç”Ÿ/å¾“æ¥­å“¡ã¯æ‰€å±žã—ã¦ã„ã‚‹ã‚°ãƒ«ãƒ¼ãƒ—ã®ã¿
                const user = await query('SELECT student_id FROM users WHERE id = ?', [userId]);
                if (user.length === 0 || !user[0].student_id) return [];

                const groups = await query(
                    'SELECT group_id FROM group_members WHERE student_id = ? AND status = "active"',
                    [user[0].student_id]
                );
                return groups.map(g => g.group_id);
            }
        } catch (error) {
            logger.error('ã‚¢ã‚¯ã‚»ã‚¹å¯èƒ½ã‚°ãƒ«ãƒ¼ãƒ—å–å¾—ã‚¨ãƒ©ãƒ¼:', error);
            return [];
        }
    }

    /**
     * ãƒ‡ãƒ¼ã‚¿ã‚¢ã‚¯ã‚»ã‚¹æ¨©é™ãƒã‚§ãƒƒã‚¯ï¼ˆæ±Žç”¨ï¼‰
     * @param {Object} user - ãƒ¦ãƒ¼ã‚¶ãƒ¼ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
     * @param {string} dataType - ãƒ‡ãƒ¼ã‚¿ã‚¿ã‚¤ãƒ— ('group', 'student', 'attendance' etc.)
     * @param {number|string} dataId - ãƒ‡ãƒ¼ã‚¿ID
     * @returns {Promise<boolean>}
     */
    static async canAccessData(user, dataType, dataId) {
        if (this.isAdmin(user)) return true;

        try {
            switch (dataType) {
                case 'group':
                    // ã‚°ãƒ«ãƒ¼ãƒ—ã¸ã®ã‚¢ã‚¯ã‚»ã‚¹æ¨©ï¼ˆæ‹…å½“æ•™å“¡ã‹ãƒ¡ãƒ³ãƒãƒ¼ã‹ï¼‰
                    if (user.role === 'teacher') {
                        return await this.isClassTeacher(user.id, dataId);
                    } else if (user.role === 'student' || user.role === 'employee') {
                        // æ‰€å±žãƒ¡ãƒ³ãƒãƒ¼ã‹ãƒã‚§ãƒƒã‚¯
                        if (!user.studentId) return false;
                        const member = await query(
                            'SELECT id FROM group_members WHERE group_id = ? AND student_id = ?',
                            [dataId, user.studentId]
                        );
                        return member.length > 0;
                    }
                    break;

                // ä»–ã®ãƒ‡ãƒ¼ã‚¿ã‚¿ã‚¤ãƒ—ã‚‚å¿…è¦ã«å¿œã˜ã¦è¿½åŠ 
                default:
                    return false;
            }
            return false;
        } catch (error) {
            logger.error('ãƒ‡ãƒ¼ã‚¿ã‚¢ã‚¯ã‚»ã‚¹æ¨©é™ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
            return false;
        }
    }
}

