/**
 * IPã‚¢ãƒ‰ãƒ¬ã‚¹ã‚’æ•´æ•°ã«å¤‰æ›ï¼ˆIPv4ã®ã¿ï¼‰
 * @param {string} ip - IPã‚¢ãƒ‰ãƒ¬ã‚¹
 * @returns {number} æ•´æ•°è¡¨ç¾ã®IP
 */
function ipToInt(ip) {
    const parts = ip.split('.');
    return (parseInt(parts[0]) * 16777216) +
        (parseInt(parts[1]) * 65536) +
        (parseInt(parts[2]) * 256) +
        parseInt(parts[3]);
}

/**
 * ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ç®¡ç†ã‚µãƒ¼ãƒ“ã‚¹
 */
class SecurityService {
    /**
     * IPã‚¢ãƒ‰ãƒ¬ã‚¹ãŒè¨±å¯ç¯„å›²å†…ã‹ãƒã‚§ãƒƒã‚¯
     * @param {string} ipAddress - ãƒã‚§ãƒƒã‚¯å¯¾è±¡ã®IPã‚¢ãƒ‰ãƒ¬ã‚¹
     * @returns {Promise<Object>} { allowed: boolean, matchedRange: Object|null }
     */
    static async isIPAllowed(ipAddress) {
        try {
            // è¨±å¯ã•ã‚Œã¦ã„ã‚‹IPç¯„å›²ã‚’å–å¾—
            const sql = `
        SELECT id, name, ip_start, ip_end, description
        FROM allowed_ip_ranges
        WHERE is_active = TRUE
      `;

            const ipRanges = await query(sql);

            // IPv4ãƒã‚§ãƒƒã‚¯ï¼ˆç°¡æ˜“ç‰ˆï¼‰
            const ipInt = ipToInt(ipAddress);

            for (const range of ipRanges) {
                const startInt = ipToInt(range.ip_start);
                const endInt = ipToInt(range.ip_end);

                if (ipInt >= startInt && ipInt <= endInt) {
                    return {
                        allowed: true,
                        matchedRange: {
                            id: range.id,
                            name: range.name,
                            description: range.description
                        }
                    };
                }
            }

            // ã©ã®IPç¯„å›²ã«ã‚‚ä¸€è‡´ã—ãªã„å ´åˆ
            return {
                allowed: false,
                matchedRange: null
            };
        } catch (error) {
            logger.error('IPè¨±å¯ãƒã‚§ãƒƒã‚¯ã‚¨ãƒ©ãƒ¼:', error);
            // ã‚¨ãƒ©ãƒ¼ã®å ´åˆã¯å®‰å…¨å´ã«å€’ã™ï¼ˆè¨±å¯ã—ãªã„ï¼‰
            return {
                allowed: false,
                matchedRange: null,
                error: error.message
            };
        }
    }

    /**
     * ã‚¹ã‚­ãƒ£ãƒ³ãƒ­ã‚°ã‚’è¨˜éŒ²
     * @param {Object} logData - ãƒ­ã‚°ãƒ‡ãƒ¼ã‚¿
     * @returns {Promise<Object>} è¨˜éŒ²çµæžœ
     */
    static async logScan(logData) {
        try {
            const { qrCodeId, studentId, ipAddress, isAllowed, userAgent, result, errorMessage } = logData;

            const sql = `
        INSERT INTO scan_logs 
        (qr_code_id, student_id, ip_address, is_allowed, user_agent, result, error_message)
        VALUES (?, ?, ?, ?, ?, ?, ?)
      `;

            const insertResult = await query(sql, [
                qrCodeId,
                studentId,
                ipAddress,
                isAllowed,
                userAgent || null,
                result,
                errorMessage || null
            ]);

            return {
                success: true,
                logId: insertResult.insertId
            };
