  /**
   * å­¦ç”Ÿä¸€è¦§ã®å–å¾—
   */
  static async getStudents(options = {}) {
    try {
      const { search, grade, class_name, status, limit, offset = 0 } = options;

      let sql = `
        SELECT *
        FROM students
        WHERE 1=1
      `;
      const params = [];
      const countParams = [];

      if (search) {
        sql += ' AND (name LIKE ? OR student_id LIKE ? OR email LIKE ?)';
        params.push(`%${search}%`, `%${search}%`, `%${search}%`);
      }
      if (grade) {
        sql += ' AND grade = ?';
        params.push(grade);
      }
      if (class_name) {
        sql += ' AND class_name = ?';
        params.push(class_name);
      }
      if (status) {
        sql += ' AND status = ?';
        params.push(status);
      }

      // ã‚«ã‚¦ãƒ³ãƒˆã‚¯ã‚¨ãƒªç”¨ (LIMIT/OFFSETå‰)
      const countSql = `SELECT COUNT(*) as total FROM (${sql}) as count_query`;
      Object.assign(countParams, params);

      sql += ' ORDER BY name ASC';

      // [ä¿®æ­£] ãƒšãƒ¼ã‚¸ãƒãƒ¼ã‚·ãƒ§ãƒ³ã®ãƒ­ã‚¸ãƒƒã‚¯ã‚’ä¿®æ­£
      if (limit) {
