      return {
        success: false,
        message: 'å­¦ç”Ÿä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * å­¦ç”Ÿæƒ…å ±ã®æ›´æ–°
   */
  static async updateStudent(studentId, updateData) {
    try {
      const result = await transaction(async (conn) => {
        // æ›´æ–°å¯èƒ½ãªãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰
        const allowedFields = ['name', 'card_id', 'email', 'phone', 'grade', 'class_name', 'enrollment_date', 'status'];
        const updateFields = [];
        const updateValues = [];

        for (const field of allowedFields) {
          if (updateData[field] !== undefined) {
            updateFields.push(`${field} = ?`);
            updateValues.push(updateData[field]);
          }
        }

        if (updateFields.length === 0) {
          return { success: false, message: 'æ›´æ–°ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“' };
        }

        updateFields.push('updated_at = CURRENT_TIMESTAMP');
        updateValues.push(studentId);

        await conn.execute(
          `UPDATE students SET ${updateFields.join(', ')} WHERE student_id = ?`,
          updateValues
        );

        return { success: true, message: 'å­¦ç”Ÿæƒ…å ±ãŒæ›´æ–°ã•ã‚Œã¾ã—ãŸ' };
      });

      logger.info('å­¦ç”Ÿæƒ…å ±æ›´æ–°æˆåŠŸ', { studentId });
      return result;
    } catch (error) {
      logger.error('å­¦ç”Ÿæƒ…å ±æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: error.message || 'å­¦ç”Ÿæƒ…å ±ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
