        );

        if (existingStudent.length === 0) {
          throw new Error('å­¦ç”ŸãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
        }

        // é–¢é€£ã™ã‚‹å‡ºæ¬ è¨˜éŒ²ã‚’å‰Šé™¤
        await query(
