  /**
   * ç§‘ç›®æƒ…å ±ã®æ›´æ–°
   */
  static async updateSubject(subjectId, updateData) {
    try {
      const result = await transaction(async (conn) => {
        // ç§‘ç›®ã®å­˜åœ¨ç¢ºèª
        const existingSubject = await query(
          'SELECT * FROM subjects WHERE id = ?',
          [subjectId]
        );

        if (existingSubject.length === 0) {
          throw new Error('ç§‘ç›®ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
        }

        // ç§‘ç›®ã‚³ãƒ¼ãƒ‰ã®é‡è¤‡ãƒã‚§ãƒƒã‚¯ï¼ˆç§‘ç›®ã‚³ãƒ¼ãƒ‰ãŒå¤‰æ›´ã•ã‚Œã‚‹å ´åˆï¼‰
        if (updateData.subject_code && updateData.subject_code !== existingSubject[0].subject_code) {
          const existingCode = await query(
            'SELECT subject_code FROM subjects WHERE subject_code = ? AND id != ?',
            [updateData.subject_code, subjectId]
          );

          if (existingCode.length > 0) {
            throw new Error('ã“ã®ç§‘ç›®ã‚³ãƒ¼ãƒ‰ã¯æ—¢ã«ä½¿ç”¨ã•ã‚Œã¦ã„ã¾ã™');
          }
        }

        // æ›´æ–°ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰ã®æ§‹ç¯‰
        const updateFields = [];
        const updateParams = [];

        const allowedFields = ['subject_code', 'subject_name', 'description', 'credits', 'is_active'];

        for (const field of allowedFields) {
          if (updateData[field] !== undefined) {
            updateFields.push(`${field} = ?`);
            updateParams.push(updateData[field]);
          }
        }

        if (updateFields.length === 0) {
          throw new Error('æ›´æ–°ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“');
        }

        updateFields.push('updated_at = CURRENT_TIMESTAMP');
        updateParams.push(subjectId);

        await query(
          `UPDATE subjects SET ${updateFields.join(', ')} WHERE id = ?`,
          updateParams
        );

        return {
          success: true,
          message: 'ç§‘ç›®æƒ…å ±ãŒæ›´æ–°ã•ã‚Œã¾ã—ãŸ'
        };
      });

      logger.info('ç§‘ç›®æƒ…å ±æ›´æ–°æˆåŠŸ', { subjectId });
      return result;
    } catch (error) {
      logger.error('ç§‘ç›®æƒ…å ±æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: error.message || 'ç§‘ç›®æƒ…å ±ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
