 */
class UserService {
  /**
   * å…¨ãƒ¦ãƒ¼ã‚¶ãƒ¼ä¸€è¦§ã®å–å¾—
   */
  static async getAllUsers() {
    try {
      const users = await query(
        'SELECT id, name, email, employee_id, department, role, created_at FROM users ORDER BY created_at DESC'
      );

      return {
        success: true,
        data: { users }
      };
    } catch (error) {
