      return {
        success: false,
        message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ä¸€è¦§ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * ç‰¹å®šãƒ¦ãƒ¼ã‚¶ãƒ¼ã®å–å¾—
   */
  static async getUser(userId) {
    try {
      // [ä¿®æ­£] student_id ã¨ last_role_update ã‚‚å–å¾—
      const users = await query(
        'SELECT id, name, email, employee_id, student_id, department, role, created_at, last_role_update FROM users WHERE id = ?',
        [userId]
      );

      if (users.length === 0) {
        return {
          success: false,
          message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“'
        };
      }

      return {
        success: true,
        data: { user: users[0] }
      };
    } catch (error) {
      logger.error('ãƒ¦ãƒ¼ã‚¶ãƒ¼å–å¾—ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã®æ›´æ–°
   */
  static async updateUser(userId, updateData) {
    try {
      const result = await transaction(async (conn) => {
        const allowedFields = ['name', 'email', 'department', 'phone'];
        const updateFields = [];
        const updateValues = [];

        if (updateData.email) {
          const existingEmail = await query(
            'SELECT id FROM users WHERE email = ? AND id != ?',
            [updateData.email, userId]
          );

          if (existingEmail.length > 0) {
            throw new Error('ã“ã®ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¯æ—¢ã«ä½¿ç”¨ã•ã‚Œã¦ã„ã¾ã™');
          }
        }

        for (const field of allowedFields) {
          if (updateData[field] !== undefined) {
            updateFields.push(`${field} = ?`);
            updateValues.push(updateData[field]);
          }
        }

        if (updateFields.length === 0) {
          return { success: false, message: 'æ›´æ–°ã™ã‚‹ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“' };
        }

        updateFields.push('updated_at = CURRENT_TIMESTAMP');
        updateValues.push(userId);

        await conn.execute(
          `UPDATE users SET ${updateFields.join(', ')} WHERE id = ?`,
          updateValues
        );

        return { success: true, message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ãŒæ›´æ–°ã•ã‚Œã¾ã—ãŸ' };
      });

      logger.info('ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±æ›´æ–°æˆåŠŸ', { userId });
      return result;
    } catch (error) {
      logger.error('ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: error.message || 'ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * ãƒ¦ãƒ¼ã‚¶ãƒ¼ã®å‰Šé™¤
   */
  static async deleteUser(userId) {
    try {
      const result = await transaction(async (conn) => {
        const existingUser = await query(
          'SELECT id FROM users WHERE id = ?',
          [userId]
        );

        if (existingUser.length === 0) {
          throw new Error('ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
        }

        await query('DELETE FROM user_attendance_records WHERE user_id = ?', [userId]);
        await query('DELETE FROM detailed_attendance_records WHERE created_by = ?', [userId]);
        await query('DELETE FROM notifications WHERE user_id = ?', [userId]);
        await query('DELETE FROM audit_logs WHERE user_id = ?', [userId]);
        await query('DELETE FROM users WHERE id = ?', [userId]);

        return {
          success: true,
          message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒå‰Šé™¤ã•ã‚Œã¾ã—ãŸ'
        };
      });

      logger.info('ãƒ¦ãƒ¼ã‚¶ãƒ¼å‰Šé™¤æˆåŠŸ', { userId });
      return result;
    } catch (error) {
      logger.error('ãƒ¦ãƒ¼ã‚¶ãƒ¼å‰Šé™¤ã‚¨ãƒ©ãƒ¼:', error.message);
      return {
        success: false,
        message: error.message || 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ã®å‰Šé™¤ã«å¤±æ•—ã—ã¾ã—ãŸ'
      };
    }
  }

  /**
   * [å®Œå…¨ç‰ˆ] ãƒ­ãƒ¼ãƒ«å¤‰æ›´ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã®å–å¾—
   */
  static async getRoleUpdateStatus(userId) {
    try {
      const [user] = await query('SELECT last_role_update FROM users WHERE id = ?', [userId]);

      if (!user) {
        return { success: false, message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“' };
      }

      const { last_role_update } = user;
      let canUpdate = true;
      let nextUpdateDate = null;
      const today = new Date();

      if (last_role_update) {
        const lastUpdate = new Date(last_role_update);
        const nextDate = new Date(lastUpdate.setDate(lastUpdate.getDate() + 90));

        if (today < nextDate) {
          canUpdate = false;
          nextUpdateDate = nextDate.toISOString().split('T')[0];
        }
      }

      return {
        success: true,
        data: {
          canUpdate,
          lastRoleUpdate: last_role_update,
          nextUpdateDate
        }
      };
    } catch (error) {
      logger.error('ãƒ­ãƒ¼ãƒ«å¤‰æ›´ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹å–å¾—ã‚¨ãƒ©ãƒ¼:', error.message);
      return { success: false, message: 'ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã®å–å¾—ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }
  }

  /**
   * [å®Œå…¨ç‰ˆ] ãƒ­ãƒ¼ãƒ«ã®æ›´æ–°
   */
  static async updateRole(userId, newRole, password) {
    try {
      const [user] = await query('SELECT password FROM users WHERE id = ?', [userId]);
      if (!user) {
        return { success: false, message: 'ãƒ¦ãƒ¼ã‚¶ãƒ¼ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“' };
      }
      const isPasswordValid = await bcrypt.compare(password, user.password);
      if (!isPasswordValid) {
        return { success: false, message: 'ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ãŒæ­£ã—ãã‚ã‚Šã¾ã›ã‚“' };
      }

      const statusResult = await this.getRoleUpdateStatus(userId);
      if (!statusResult.success || !statusResult.data.canUpdate) {
        return { success: false, message: 'ãƒ­ãƒ¼ãƒ«å¤‰æ›´ã¯90æ—¥ã«1å›žã®ã¿å¯èƒ½ã§ã™' };
      }

      if (newRole !== 'student' && newRole !== 'employee' && newRole !== 'admin') {
        return { success: false, message: 'ç„¡åŠ¹ãªå½¹å‰²ã§ã™' };
      }

      const result = await transaction(async (conn) => {
        const today = new Date().toISOString().split('T')[0];

        await conn.execute(
          'UPDATE users SET role = ?, last_role_update = ? WHERE id = ?',
          [newRole, today, userId]
        );

        if (newRole === 'student') {
          await conn.execute(
            'UPDATE users SET employee_id = NULL, department = NULL WHERE id = ?',
            [userId]
          );
        } else {
          await conn.execute(
            'UPDATE users SET student_id = NULL WHERE id = ?',
            [userId]
          );
        }

        return { success: true, message: 'å½¹å‰²ãŒæ­£å¸¸ã«æ›´æ–°ã•ã‚Œã¾ã—ãŸã€‚' };
      });

      logger.info('ãƒ­ãƒ¼ãƒ«æ›´æ–°æˆåŠŸ', { userId, newRole });
      return result;

    } catch (error) {
      logger.error('ãƒ­ãƒ¼ãƒ«æ›´æ–°ã‚¨ãƒ©ãƒ¼:', error.message);
      return { success: false, message: error.message || 'ãƒ­ãƒ¼ãƒ«ã®æ›´æ–°ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }
  }
}

