/**
 * CSVç”Ÿæˆãƒ¦ãƒ¼ãƒ†ã‚£ãƒªãƒ†ã‚£
 * BOMä»˜ãUTF-8ã§Excelå¯¾å¿œ
 */

/**
 * é…åˆ—ãƒ‡ãƒ¼ã‚¿ã‚’CSVå½¢å¼ã®æ–‡å­—åˆ—ã«å¤‰æ›
 * @param {Array<Object>} data - ãƒ‡ãƒ¼ã‚¿é…åˆ—
 * @param {Array<string>} headers - ãƒ˜ãƒƒãƒ€ãƒ¼é…åˆ—
 * @param {Array<string>} fields - ãƒ•ã‚£ãƒ¼ãƒ«ãƒ‰åé…åˆ—
 * @returns {string} CSVæ–‡å­—åˆ—
 */
const arrayToCSV = (data, headers, fields) => {
    // BOM (Byte Order Mark) ã‚’è¿½åŠ ã—ã¦Excelã§æ–‡å­—åŒ–ã‘ã‚’é˜²ã
    const BOM = '\uFEFF';

    // ãƒ˜ãƒƒãƒ€ãƒ¼è¡Œ
    const headerRow = headers.join(',');

    // ãƒ‡ãƒ¼ã‚¿è¡Œ
    const dataRows = data.map(row => {
        return fields.map(field => {
            let value = row[field] ?? '';

            // å€¤ã‚’æ–‡å­—åˆ—ã«å¤‰æ›
            value = String(value);

            // ã‚«ãƒ³ãƒžã€æ”¹è¡Œã€ãƒ€ãƒ–ãƒ«ã‚¯ã‚©ãƒ¼ãƒˆã‚’å«ã‚€å ´åˆã¯ãƒ€ãƒ–ãƒ«ã‚¯ã‚©ãƒ¼ãƒˆã§å›²ã‚€
            if (value.includes(',') || value.includes('\n') || value.includes('"')) {
                // ãƒ€ãƒ–ãƒ«ã‚¯ã‚©ãƒ¼ãƒˆã‚’ã‚¨ã‚¹ã‚±ãƒ¼ãƒ—
                value = value.replace(/"/g, '""');
                value = `"${value}"`;
            }

            return value;
        }).join(',');
    });

    return BOM + [headerRow, ...dataRows].join('\n');
};

/**
 * å‡ºå¸­è¨˜éŒ²ã‚’CSVå½¢å¼ã«å¤‰æ›
 * @param {Array<Object>} records - å‡ºå¸­è¨˜éŒ²é…åˆ—
 * @returns {string} CSVæ–‡å­—åˆ—
 */
const attendanceToCSV = (records) => {
    const headers = ['æ—¥ä»˜', 'æ›œæ—¥', 'é–‹å§‹æ™‚åˆ»', 'çµ‚äº†æ™‚åˆ»', 'ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹', 'å‚™è€ƒ'];
    const fields = ['date', 'day_of_week', 'start_time', 'end_time', 'status', 'notes'];

    // ãƒ‡ãƒ¼ã‚¿ã‚’æ•´å½¢
    const formattedData = records.map(record => ({
        date: record.date,
        day_of_week: getDayOfWeek(record.date),
        start_time: record.start_time || '-',
        end_time: record.end_time || '-',
        status: getStatusLabel(record.status),
        notes: record.notes || ''
    }));

    return arrayToCSV(formattedData, headers, fields);
};

/**
 * ã‚¤ãƒ™ãƒ³ãƒˆå‚åŠ è€…ã‚’CSVå½¢å¼ã«å¤‰æ›
 * @param {Object} event - ã‚¤ãƒ™ãƒ³ãƒˆæƒ…å ±
 * @param {Array<Object>} participants - å‚åŠ è€…é…åˆ—
 * @returns {string} CSVæ–‡å­—åˆ—
 */
const eventParticipantsToCSV = (event, participants) => {
    const headers = ['å­¦ç”ŸID', 'æ°å', 'ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹', 'å‚åŠ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹', 'å¿œç­”æ—¥æ™‚'];
    const fields = ['student_id', 'name', 'email', 'response_status', 'responded_at'];

    // ãƒ‡ãƒ¼ã‚¿ã‚’æ•´å½¢
    const formattedData = participants.map(participant => ({
        student_id: participant.student_id || '-',
        name: participant.name,
        email: participant.email,
        response_status: getParticipantStatusLabel(participant.response_status),
        responded_at: participant.responded_at ? new Date(participant.responded_at).toLocaleString('ja-JP') : '-'
    }));

    return arrayToCSV(formattedData, headers, fields);
};

/**
 * æ—¥ä»˜ã‹ã‚‰æ›œæ—¥ã‚’å–å¾—
 * @param {string} dateStr - æ—¥ä»˜æ–‡å­—åˆ— (YYYY-MM-DD)
 * @returns {string} æ›œæ—¥
 */
const getDayOfWeek = (dateStr) => {
    const days = ['æ—¥', 'æœˆ', 'ç«', 'æ°´', 'æœ¨', 'é‡‘', 'åœŸ'];
    const date = new Date(dateStr);
    return days[date.getDay()];
};

/**
 * ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚³ãƒ¼ãƒ‰ã‹ã‚‰ãƒ©ãƒ™ãƒ«ã‚’å–å¾—
 * @param {string} status - ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚³ãƒ¼ãƒ‰
 * @returns {string} ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ãƒ©ãƒ™ãƒ«
 */
const getStatusLabel = (status) => {
    const statusMap = {
        'present': 'å‡ºå¸­',
        'absent': 'æ¬ å¸­',
        'late': 'é…åˆ»',
        'excused': 'å…¬æ¬ '
    };
    return statusMap[status] || status;
};

/**
 * å‚åŠ ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚³ãƒ¼ãƒ‰ã‹ã‚‰ãƒ©ãƒ™ãƒ«ã‚’å–å¾—
 * @param {string} status - ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ã‚³ãƒ¼ãƒ‰
 * @returns {string} ã‚¹ãƒ†ãƒ¼ã‚¿ã‚¹ãƒ©ãƒ™ãƒ«
 */
const getParticipantStatusLabel = (status) => {
    const statusMap = {
        'pending': 'æœªå¿œç­”',
        'accepted': 'å‚åŠ ',
        'declined': 'ä¸å‚åŠ '
    };
    return statusMap[status] || status;
};

module.exports = {
    arrayToCSV,
    attendanceToCSV,
    eventParticipantsToCSV
};

const jwt = require('jsonwebtoken');
const logger = require('../utils/logger');

/**
 * JWTèªè¨¼ãƒ¦ãƒ¼ãƒ†ã‚£ãƒªãƒ†ã‚£
 */
class JWTUtil {
  /**
   * JWTãƒˆãƒ¼ã‚¯ãƒ³ã‚’ç”Ÿæˆ
   */
  static generateToken(payload) {
    try {
      // JWT_EXPIRES_INã‚’æ•°å€¤ã«å¤‰æ›ï¼ˆãƒ‡ãƒ•ã‚©ãƒ«ãƒˆã¯7æ—¥é–“ = 604800ç§’ï¼‰
      const expiresIn = process.env.JWT_EXPIRES_IN
        ? parseInt(process.env.JWT_EXPIRES_IN, 10)
        : 604800;

      const token = jwt.sign(
        payload,
        process.env.JWT_SECRET,
        {
          expiresIn: expiresIn,
          issuer: 'attendance-app',
          audience: 'attendance-app-client'
        }
      );

      logger.debug('JWTãƒˆãƒ¼ã‚¯ãƒ³ã‚’ç”Ÿæˆã—ã¾ã—ãŸ', { userId: payload.id, expiresIn });
      return token;
    } catch (error) {
      logger.error('JWTãƒˆãƒ¼ã‚¯ãƒ³ç”Ÿæˆã‚¨ãƒ©ãƒ¼:', error.message);
      throw new Error('ãƒˆãƒ¼ã‚¯ãƒ³ã®ç”Ÿæˆã«å¤±æ•—ã—ã¾ã—ãŸ');
    }
  }

  /**
   * JWTãƒˆãƒ¼ã‚¯ãƒ³ã‚’æ¤œè¨¼ãƒ»ãƒ‡ã‚³ãƒ¼ãƒ‰
   */
  static verifyToken(token) {
    try {
      const decoded = jwt.verify(token, process.env.JWT_SECRET, {
        issuer: 'attendance-app',
        audience: 'attendance-app-client'
      });

      logger.debug('JWTãƒˆãƒ¼ã‚¯ãƒ³ã‚’æ¤œè¨¼ã—ã¾ã—ãŸ', { userId: decoded.id });
      return decoded;
    } catch (error) {
      if (error.name === 'TokenExpiredError') {
        logger.warn('JWTãƒˆãƒ¼ã‚¯ãƒ³ã®æœ‰åŠ¹æœŸé™ãŒåˆ‡ã‚Œã¦ã„ã¾ã™');
        throw new Error('ãƒˆãƒ¼ã‚¯ãƒ³ã®æœ‰åŠ¹æœŸé™ãŒåˆ‡ã‚Œã¦ã„ã¾ã™');
      } else if (error.name === 'JsonWebTokenError') {
        logger.warn('ç„¡åŠ¹ãªJWTãƒˆãƒ¼ã‚¯ãƒ³ã§ã™');
        throw new Error('ç„¡åŠ¹ãªãƒˆãƒ¼ã‚¯ãƒ³ã§ã™');
      } else {
        logger.error('JWTãƒˆãƒ¼ã‚¯ãƒ³æ¤œè¨¼ã‚¨ãƒ©ãƒ¼:', error.message);
        throw new Error('ãƒˆãƒ¼ã‚¯ãƒ³ã®æ¤œè¨¼ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }
    }
  }

  /**
   * ãƒªã‚¯ã‚¨ã‚¹ãƒˆãƒ˜ãƒƒãƒ€ãƒ¼ã‹ã‚‰ãƒˆãƒ¼ã‚¯ãƒ³ã‚’å–å¾—
   */
  static getTokenFromHeader(req) {
    const authHeader = req.headers.authorization;

    if (!authHeader) {
      return null;
    }

    const parts = authHeader.split(' ');
    if (parts.length !== 2 || parts[0] !== 'Bearer') {
      return null;
    }

    return parts[1];
  }

  /**
   * ãƒˆãƒ¼ã‚¯ãƒ³ã®æœ‰åŠ¹æœŸé™ã‚’ãƒã‚§ãƒƒã‚¯
   */
  static isTokenExpired(token) {
    try {
      const decoded = jwt.decode(token);
      if (!decoded || !decoded.exp) {
        return true;
      }

      const currentTime = Math.floor(Date.now() / 1000);
      return decoded.exp < currentTime;
    } catch (error) {
      return true;
    }
  }

  /**
   * ãƒˆãƒ¼ã‚¯ãƒ³ã‹ã‚‰ãƒšã‚¤ãƒ­ãƒ¼ãƒ‰ã‚’å–å¾—ï¼ˆæ¤œè¨¼ãªã—ï¼‰
   */
  static decodeToken(token) {
    try {
      return jwt.decode(token);
    } catch (error) {
      logger.error('JWTãƒˆãƒ¼ã‚¯ãƒ³ãƒ‡ã‚³ãƒ¼ãƒ‰ã‚¨ãƒ©ãƒ¼:', error.message);
      return null;
    }
  }
}

module.exports = JWTUtil;

const winston = require('winston');
const path = require('path');

// ãƒ­ã‚°ãƒ•ã‚©ãƒ¼ãƒžãƒƒãƒˆ
const logFormat = winston.format.combine(
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss'
  }),
  winston.format.errors({ stack: true }),
  winston.format.json()
);

// ã‚³ãƒ³ã‚½ãƒ¼ãƒ«ãƒ•ã‚©ãƒ¼ãƒžãƒƒãƒˆ
const consoleFormat = winston.format.combine(
  winston.format.colorize(),
  winston.format.timestamp({
    format: 'YYYY-MM-DD HH:mm:ss'
  }),
  winston.format.printf(({ timestamp, level, message, ...meta }) => {
    let metaStr = '';
    if (Object.keys(meta).length > 0) {
      // [ä¿®æ­£] ãƒ¡ã‚¿æƒ…å ±ãŒã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã®å ´åˆã®å¯¾å¿œ
      try {
        metaStr = ' ' + JSON.stringify(meta);
      } catch (e) {
         metaStr = ' [Meta serialization error]';
      }
    }
    // [ä¿®æ­£] message ãŒã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã®å ´åˆã‚‚è€ƒæ…®
    const msg = (typeof message === 'object') ? JSON.stringify(message) : message;
    return `${timestamp} [${level}]: ${msg}${metaStr}`;
  })
);

// ãƒ­ã‚¬ãƒ¼è¨­å®š
const isProduction = process.env.NODE_ENV === 'production';
const logLevel = process.env.LOG_LEVEL || (isProduction ? 'info' : 'debug');
const maxSize = parseInt(process.env.LOG_MAX_SIZE) || (isProduction ? 10485760 : 5242880);
const maxFiles = parseInt(process.env.LOG_MAX_FILES) || (isProduction ? 10 : 5);

// --- [ä¿®æ­£] ã‚³ãƒ³ã‚½ãƒ¼ãƒ«ãƒˆãƒ©ãƒ³ã‚¹ãƒãƒ¼ãƒˆã‚’å®šç¾© ---
const consoleTransport = new winston.transports.Console({
  format: consoleFormat,
  level: logLevel
});

const transports = [
  // [ä¿®æ­£] ç’°å¢ƒï¼ˆNODE_ENVï¼‰ã«é–¢ã‚ã‚‰ãšã€å¸¸ã«ã‚³ãƒ³ã‚½ãƒ¼ãƒ«ã«å‡ºåŠ›
  consoleTransport
];

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ãƒ•ã‚¡ã‚¤ãƒ«
transports.push(
    new winston.transports.File({
      filename: path.join(__dirname, 'logs', 'error.log'),
      level: 'error',
      maxsize: maxSize,
      maxFiles: maxFiles,
      format: logFormat
  })
);

// å…¨ãƒ­ã‚°ãƒ•ã‚¡ã‚¤ãƒ«
transports.push(
    new winston.transports.File({
      filename: path.join(__dirname, 'logs', 'combined.log'),
      maxsize: maxSize,
      maxFiles: maxFiles,
      format: logFormat
  })
);

if (isProduction) {
