const CACHE_NAME = 'attendance-app-v1';
const urlsToCache = [
  '/link-up/',
  '/link-up/static/js/bundle.js',
  '/link-up/static/css/main.css',
  '/link-up/manifest.json',
  '/link-up/logo192.png',
  '/link-up/logo512.png'
];

// ã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«æ™‚ã®å‡¦ç†
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then((cache) => {
        console.log('Opened cache');
        return cache.addAll(urlsToCache);
      })
      .then(() => {
        // ã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«å®Œäº†å¾Œã€ã™ãã«ã‚¢ã‚¯ãƒ†ã‚£ãƒ™ãƒ¼ãƒˆ
        return self.skipWaiting();
      })
  );
});

// ãƒ•ã‚§ãƒƒãƒæ™‚ã®å‡¦ç†ï¼ˆã‚ªãƒ•ãƒ©ã‚¤ãƒ³å¯¾å¿œï¼‰
self.addEventListener('fetch', (event) => {
  event.respondWith(
    caches.match(event.request)
      .then((response) => {
        // ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã«å­˜åœ¨ã™ã‚‹å ´åˆã¯ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã‚’è¿”ã™
        if (response) {
          return response;
        }

        // ãƒãƒƒãƒˆãƒ¯ãƒ¼ã‚¯ãƒªã‚¯ã‚¨ã‚¹ãƒˆã‚’è©¦è¡Œ
        return fetch(event.request)
          .then((response) => {
            // ãƒ¬ã‚¹ãƒãƒ³ã‚¹ãŒæœ‰åŠ¹ã§ãªã„å ´åˆã¯ãã®ã¾ã¾è¿”ã™
            if (!response || response.status !== 200 || response.type !== 'basic') {
              return response;
            }

            // ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã‚’ã‚¯ãƒ­ãƒ¼ãƒ³ã—ã¦ã‚­ãƒ£ãƒƒã‚·ãƒ¥ã«ä¿å­˜
            const responseToCache = response.clone();
            caches.open(CACHE_NAME)
              .then((cache) => {
                cache.put(event.request, responseToCache);
              });

            return response;
          })
          .catch(() => {
            // ã‚ªãƒ•ãƒ©ã‚¤ãƒ³æ™‚ã®ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯
            if (event.request.destination === 'document') {
              return caches.match('/link-up/');
            }
          });
      })
  );
});

// ã‚¢ã‚¯ãƒ†ã‚£ãƒ™ãƒ¼ãƒˆæ™‚ã®å‡¦ç†
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => {
      // ã™ã¹ã¦ã®ã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆã‚’åˆ¶å¾¡ä¸‹ã«ç½®ã
      return self.clients.claim();
    })
  );
});

