import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:3001/api';

const apiClient = axios.create({
    baseURL: API_BASE_URL,
    withCredentials: true,
    headers: {
        'Content-Type': 'application/json',
    },
    timeout: 10000,
});

// Excelç”¨ã®ã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆ
const excelClient = axios.create({
    baseURL: API_BASE_URL,
    withCredentials: true,
    headers: {
        'Content-Type': 'multipart/form-data',
    },
    timeout: 60000, // Excelã‚¤ãƒ³ãƒãƒ¼ãƒˆã¯æ™‚é–“ãŒã‹ã‹ã‚‹å¯èƒ½æ€§ãŒã‚ã‚‹ãŸã‚é•·ã‚ã«è¨­å®š
});

const responseInterceptor = (response) => {
    if (response.data && response.data.success) {
        return response.data;
    }
    if (response.status === 200 && response.data) {
        return { success: true, data: response.data };
    }
    return response;
};

const errorInterceptor = (error) => {
    let errorResponse = {
        success: false,
        message: 'ä¸æ˜Žãªã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ',
        status: null,
        data: null
    };

    if (error.response) {
        errorResponse.status = error.response.status;
        if (error.response.data && error.response.data.message) {
            errorResponse.message = error.response.data.message;
        } else {
            errorResponse.message = `APIæŽ¥ç¶šã‚¨ãƒ©ãƒ¼: ${error.response.statusText}`;
        }
        errorResponse.data = error.response.data;
    } else if (error.request) {
        errorResponse.message = 'APIæŽ¥ç¶šã‚¨ãƒ©ãƒ¼: ã‚µãƒ¼ãƒãƒ¼ã«æŽ¥ç¶šã§ãã¾ã›ã‚“';
    } else {
        errorResponse.message = `APIæŽ¥ç¶šã‚¨ãƒ©ãƒ¼: ${error.message}`;
    }

    console.error('Timetable API Error:', errorResponse.message);
    return Promise.reject(errorResponse);
};

apiClient.interceptors.response.use(responseInterceptor, errorInterceptor);
excelClient.interceptors.response.use(responseInterceptor, errorInterceptor);

/**
 * æ™‚é–“å‰²ç®¡ç†API
 */
