  }

  static getDerivedStateFromError(error) {
    // Update state so the next render will show the fallback UI.
    return { hasError: true, error: error };
  }

  componentDidCatch(error, errorInfo) {
    // You can also log the error to an error reporting service
    // logErrorToMyService(error, errorInfo);
    this.setState({
      error: error,
      errorInfo: errorInfo 
    });
  }

  render() {
    if (this.state.hasError) {
      // You can render any custom fallback UI
      return (
        <div className="error-boundary" style={{ padding: '20px', backgroundColor: '#fff8f8', border: '1px solid #d32f2f', borderRadius: '8px', margin: '20px' }}>
          <h2 style={{ color: '#d32f2f' }}>ã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ</h2>
          <p>ã”è¿·æƒ‘ã‚’ãŠã‹ã‘ã—ç”³ã—è¨³ã‚ã‚Šã¾ã›ã‚“ã€‚ãƒšãƒ¼ã‚¸ã‚’ãƒªãƒ­ãƒ¼ãƒ‰ã—ã¦ã¿ã¦ãã ã•ã„ã€‚</p>
          <details style={{ whiteSpace: 'pre-wrap', background: '#fefefe', padding: '10px', marginTop: '10px' }}>
            {this.state.error && this.state.error.toString()}
            <br />
            {/* [ä¿®æ­£] errorInfo ãŒ null ã®å ´åˆã‚’è€ƒæ…®ã—ã€ã‚ªãƒ—ã‚·ãƒ§ãƒŠãƒ«ãƒã‚§ãƒ¼ãƒ³ (?.) ã‚’è¿½åŠ ã—ã¾ã—ãŸ
            */}
            {this.state.errorInfo?.componentStack}
          </details>
          <button 
            onClick={() => window.location.reload()}
            style={{ marginTop: '15px', padding: '10px 15px', border: 'none', borderRadius: '4px', backgroundColor: '#d32f2f', color: 'white', cursor: 'pointer' }}
          >
            ãƒšãƒ¼ã‚¸ã‚’ãƒªãƒ­ãƒ¼ãƒ‰
          </button>
        </div>
      );
    }
    return this.props.children; 
  }
}
export default ErrorBoundary;

import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Button from './Button';
import useToastStore from '../../stores/toastStore';

/**
 * ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆãƒœã‚¿ãƒ³ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆ
 * CSVãƒ•ã‚¡ã‚¤ãƒ«ã‚’ãƒ€ã‚¦ãƒ³ãƒ­ãƒ¼ãƒ‰ã™ã‚‹
 */
const ExportButton = ({ onExport, filename, label = 'ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆ', variant = 'secondary', size = 'medium', className = '' }) => {
    const [isExporting, setIsExporting] = useState(false);
    const addToast = useToastStore((state) => state.addToast);

    const handleExport = async () => {
        setIsExporting(true);
        try {
            // onExporté–¢æ•°ã‚’å‘¼ã³å‡ºã—ã¦Blobãƒ‡ãƒ¼ã‚¿ã‚’å–å¾—
            const blob = await onExport();

            if (!blob) {
                addToast('ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã™ã‚‹ãƒ‡ãƒ¼ã‚¿ãŒã‚ã‚Šã¾ã›ã‚“', 'warning');
                return;
            }

            // Blob URLã‚’ä½œæˆ
            const url = window.URL.createObjectURL(blob);

            // ãƒ€ã‚¦ãƒ³ãƒ­ãƒ¼ãƒ‰ãƒªãƒ³ã‚¯ã‚’ä½œæˆã—ã¦è‡ªå‹•ã‚¯ãƒªãƒƒã‚¯
            const link = document.createElement('a');
            link.href = url;
            link.download = filename || `export_${new Date().getTime()}.csv`;
            document.body.appendChild(link);
            link.click();

            // ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
            document.body.removeChild(link);
            window.URL.revokeObjectURL(url);

            addToast('ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆãŒå®Œäº†ã—ã¾ã—ãŸ', 'success');
        } catch (error) {
            console.error('Export error:', error);
            // ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒªãƒ³ã‚°ã¯APIã‚¯ãƒ©ã‚¤ã‚¢ãƒ³ãƒˆã§ãƒˆãƒ¼ã‚¹ãƒˆè¡¨ç¤ºã•ã‚Œã‚‹ãŸã‚ã€ã“ã“ã§ã¯è¡¨ç¤ºã—ãªã„
        } finally {
            setIsExporting(false);
        }
    };

    return (
        <Button
            onClick={handleExport}
            variant={variant}
            size={size}
            loading={isExporting}
            disabled={isExporting}
            className={className}
        >
            {label}
        </Button>
    );
};

ExportButton.propTypes = {
    onExport: PropTypes.func.isRequired,
    filename: PropTypes.string,
    label: PropTypes.string,
    variant: PropTypes.oneOf(['primary', 'secondary', 'danger']),
    size: PropTypes.oneOf(['small', 'medium', 'large']),
    className: PropTypes.string
};

export default ExportButton;

