import React, { useState } from 'react';
import { Scanner } from '@yudiel/react-qr-scanner';
import './QRScanner.css';

const QRScannerComponent = ({ onScan, onClose, isOpen }) => {
  const [error, setError] = useState(null);
  const [isScanning, setIsScanning] = useState(false);

  const handleScan = (detectedCodes) => {
    if (detectedCodes && detectedCodes.length > 0) {
      setIsScanning(false);
      try {
        // QRã‚³ãƒ¼ãƒ‰ã‹ã‚‰å­¦ç”ŸIDã‚’æŠ½å‡º
        const studentId = detectedCodes[0].rawValue.trim();
        onScan(studentId);
      } catch (err) {
        setError('QRã‚³ãƒ¼ãƒ‰ã®è§£æžã«å¤±æ•—ã—ã¾ã—ãŸ');
        console.error('QRè§£æžã‚¨ãƒ©ãƒ¼:', err);
      }
    }
  };

  const handleError = (error) => {
    console.error('QRã‚¹ã‚­ãƒ£ãƒŠãƒ¼ã‚¨ãƒ©ãƒ¼:', error);
    setError('ã‚«ãƒ¡ãƒ©ã¸ã®ã‚¢ã‚¯ã‚»ã‚¹ã«å¤±æ•—ã—ã¾ã—ãŸã€‚ã‚«ãƒ¡ãƒ©ã®è¨±å¯ã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚');
  };

  const handleStartScan = () => {
    setError(null);
    setIsScanning(true);
  };

  const handleStopScan = () => {
    setIsScanning(false);
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className="qr-scanner-overlay">
      <div className="qr-scanner-modal">
        <div className="qr-scanner-header">
          <h2>QRã‚³ãƒ¼ãƒ‰èª­ã¿å–ã‚Š</h2>
          <button 
            className="close-button" 
            onClick={onClose}
            aria-label="é–‰ã˜ã‚‹"
          >
            Ã—
          </button>
        </div>

        <div className="qr-scanner-content">
          {error && (
            <div className="error-message">
              {error}
            </div>
          )}

          {isScanning ? (
            <div className="scanner-container">
              <Scanner
                onScan={handleScan}
                onError={handleError}
                constraints={{
                  facingMode: 'environment' // èƒŒé¢ã‚«ãƒ¡ãƒ©ã‚’å„ªå…ˆ
                }}
              />
              <div className="scanner-instructions">
                <p>QRã‚³ãƒ¼ãƒ‰ã‚’ã‚«ãƒ¡ãƒ©ã®ä¸­å¤®ã«åˆã‚ã›ã¦ãã ã•ã„</p>
                <button 
                  className="btn btn--secondary"
                  onClick={handleStopScan}
                >
                  ã‚¹ã‚­ãƒ£ãƒ³åœæ­¢
                </button>
              </div>
            </div>
          ) : (
            <div className="scanner-start">
              <div className="scanner-icon">ðŸ“±</div>
              <p>QRã‚³ãƒ¼ãƒ‰ã‚¹ã‚­ãƒ£ãƒ³ã‚’é–‹å§‹ã™ã‚‹ã«ã¯ã€ä¸‹ã®ãƒœã‚¿ãƒ³ã‚’æŠ¼ã—ã¦ãã ã•ã„</p>
              <button 
                className="btn btn--primary"
                onClick={handleStartScan}
              >
                ã‚¹ã‚­ãƒ£ãƒ³é–‹å§‹
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default QRScannerComponent;

