 * èªè¨¼ãŒå¿…è¦ãªæ“ä½œã‚’è¡Œã†éš›ã«ã€æœªãƒ­ã‚°ã‚¤ãƒ³ã®å ´åˆã¯ãƒ­ã‚°ã‚¤ãƒ³ãƒšãƒ¼ã‚¸ã«èª˜å°Ž
 */
export const useLoginRedirect = () => {
  const navigate = useNavigate();
  const isAuthenticated = useAuthStore((state) => state.isAuthenticated);

  /**
   * èªè¨¼ãŒå¿…è¦ãªæ“ä½œã‚’å®Ÿè¡Œã™ã‚‹å‰ã«ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã‚’ãƒã‚§ãƒƒã‚¯
   * @param {Function} action - èªè¨¼æ¸ˆã¿ã®å ´åˆã«å®Ÿè¡Œã™ã‚‹é–¢æ•°
   * @param {string} redirectPath - ãƒ­ã‚°ã‚¤ãƒ³å¾Œã®ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆï¼ˆã‚ªãƒ—ã‚·ãƒ§ãƒ³ï¼‰
   */
  const requireAuth = (action, redirectPath = null) => {
    if (!isAuthenticated) {
      // ãƒ­ã‚°ã‚¤ãƒ³å¾Œã«å…ƒã®ãƒšãƒ¼ã‚¸ã«æˆ»ã‚‹ãŸã‚ã®æƒ…å ±ã‚’ä¿å­˜
      const currentPath = window.location.pathname;
      const redirectTo = redirectPath || currentPath;

      navigate('/login', { 
        state: { 
          from: redirectTo,
          message: 'ã“ã®æ“ä½œã‚’è¡Œã†ã«ã¯ãƒ­ã‚°ã‚¤ãƒ³ãŒå¿…è¦ã§ã™'
        } 
      });
      return false;
    }

    // èªè¨¼æ¸ˆã¿ã®å ´åˆã¯ã‚¢ã‚¯ã‚·ãƒ§ãƒ³ã‚’å®Ÿè¡Œ
    if (typeof action === 'function') {
      action();
    }
    return true;
  };

  /**
   * ç‰¹å®šã®ãƒ­ãƒ¼ãƒ«ãŒå¿…è¦ãªæ“ä½œã‚’å®Ÿè¡Œã™ã‚‹å‰ã«ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã¨ãƒ­ãƒ¼ãƒ«ã‚’ãƒã‚§ãƒƒã‚¯
   * @param {string|string[]} requiredRoles - å¿…è¦ãªãƒ­ãƒ¼ãƒ«
   * @param {Function} action - èªè¨¼æ¸ˆã¿ã‹ã¤ãƒ­ãƒ¼ãƒ«ãŒé©åˆ‡ãªå ´åˆã«å®Ÿè¡Œã™ã‚‹é–¢æ•°
   * @param {string} redirectPath - ãƒ­ã‚°ã‚¤ãƒ³å¾Œã®ãƒªãƒ€ã‚¤ãƒ¬ã‚¯ãƒˆå…ˆï¼ˆã‚ªãƒ—ã‚·ãƒ§ãƒ³ï¼‰
   */
  const requireRole = (requiredRoles, action, redirectPath = null) => {
    if (!isAuthenticated) {
      const currentPath = window.location.pathname;
      const redirectTo = redirectPath || currentPath;

      navigate('/login', { 
        state: { 
          from: redirectTo,
          message: 'ã“ã®æ“ä½œã‚’è¡Œã†ã«ã¯ãƒ­ã‚°ã‚¤ãƒ³ãŒå¿…è¦ã§ã™'
        } 
      });
      return false;
    }

    // ãƒ­ãƒ¼ãƒ«ãƒã‚§ãƒƒã‚¯ï¼ˆå®Ÿè£…ã¯å¾Œã§è¿½åŠ ï¼‰
    // const { user } = useAuth();
    // const userRole = user?.role;
    // const roles = Array.isArray(requiredRoles) ? requiredRoles : [requiredRoles];
    // if (!roles.includes(userRole)) {
    //   navigate('/unauthorized');
    //   return false;
    // }

    if (typeof action === 'function') {
      action();
    }
    return true;
  };

  /**
   * ãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã‚’ãƒã‚§ãƒƒã‚¯ã—ã¦ãƒ–ãƒ¼ãƒ«å€¤ã‚’è¿”ã™
   * @returns {boolean} ãƒ­ã‚°ã‚¤ãƒ³æ¸ˆã¿ã‹ã©ã†ã‹
   */
  const checkAuth = () => {
    return isAuthenticated;
  };

  return {
    requireAuth,
    requireRole,
    checkAuth,
    isAuthenticated
  };
};

