        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
    };

    const handlePrintQR = () => {
        if (!generatedQR || !generatedQR.qr_image) return;

        const printWindow = window.open('', '_blank');
        printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>QRã‚³ãƒ¼ãƒ‰å°åˆ·</title>
          <style>
            body {
              display: flex;
