import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import useAuthStore from '../stores/authStore';
import Button from '../components/common/Button';
import Input from '../components/common/Input';
import './RegisterPage.css';

const RegisterPage = () => {
  const navigate = useNavigate();
  const { register, isAuthenticated } = useAuthStore();
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'student',
    employeeId: '',
    studentId: '',
    department: '',
  });
  const [validationErrors, setValidationErrors] = useState({});

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    }
  }, [isAuthenticated, navigate]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));

    if (validationErrors[name]) {
      setValidationErrors(prev => ({
        ...prev,
        [name]: null,
      }));
    }
    if (name === 'role') {
      setValidationErrors(prev => ({
        ...prev,
        studentId: null,
        employeeId: null,
      }));
    }
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.name) errors.name = 'æ°åã¯å¿…é ˆã§ã™';
    if (!formData.email) errors.email = 'ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹ã¯å¿…é ˆã§ã™';

    if (formData.role === 'student') {
      if (!formData.studentId) errors.studentId = 'å­¦ç”ŸIDã¯å¿…é ˆã§ã™';
    } else if (formData.role === 'employee') {
      if (!formData.employeeId) errors.employeeId = 'ç¤¾å“¡IDã¯å¿…é ˆã§ã™';
    }

    if (formData.password.length < 6) errors.password = 'ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ã¯6æ–‡å­—ä»¥ä¸Šã§å…¥åŠ›ã—ã¦ãã ã•ã„';
    if (formData.password !== formData.confirmPassword) errors.confirmPassword = 'ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ãŒä¸€è‡´ã—ã¾ã›ã‚“';

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);
    const { confirmPassword, ...dataToSend } = formData;

    if (dataToSend.role === 'student') {
      dataToSend.employeeId = null;
      dataToSend.department = null;
    } else {
      dataToSend.studentId = null;
    }

    const result = await register(dataToSend);

    if (!result.success) {
      setError(result.message || 'ç™»éŒ²ã«å¤±æ•—ã—ã¾ã—ãŸ');
    }

    setIsLoading(false);
  };

  return (
    <div className="register-page">
      <div className="register-container">
        <div className="register-card">
          <div className="register-header">
            <h1 className="register-title">æ–°è¦ç™»éŒ²</h1>
            <p className="register-subtitle">æ–°ã—ã„ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã‚’ä½œæˆã—ã¾ã™</p>
          </div>

          <form className="register-form" onSubmit={handleSubmit}>
            {error && <div className="error-message">{error}</div>}

            <div className="form-role-selection">
              <label>
                <input
                  type="radio"
                  name="role"
                  value="student"
                  checked={formData.role === 'student'}
                  onChange={handleInputChange}
                />
                <span className="radio-label">å­¦ç”Ÿ</span>
              </label>
              <label>
                <input
                  type="radio"
                  name="role"
                  value="employee"
                  checked={formData.role === 'employee'}
                  onChange={handleInputChange}
                />
                <span className="radio-label">æ•™å“¡ãƒ»ç®¡ç†è€…</span>
              </label>
            </div>

            <Input
              label="æ°å"
              type="text"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              error={validationErrors.name}
              required
              placeholder="ä¾‹: å±±ç”° å¤ªéƒŽ"
              autoComplete="name"
            />

            <Input
              label="ãƒ¡ãƒ¼ãƒ«ã‚¢ãƒ‰ãƒ¬ã‚¹"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              error={validationErrors.email}
              required
              placeholder="ä¾‹: user@example.com"
              autoComplete="email"
            />

            {formData.role === 'student' && (
              <Input
                label="å­¦ç”ŸID"
                type="text"
                name="studentId"
                value={formData.studentId}
                onChange={handleInputChange}
                error={validationErrors.studentId}
                required
                placeholder="å­¦ç”ŸIDã‚’å…¥åŠ›"
                autoComplete="off"
              />
            )}

            {formData.role === 'employee' && (
              <>
                <Input
                  label="ç¤¾å“¡ID"
                  type="text"
                  name="employeeId"
                  value={formData.employeeId}
                  onChange={handleInputChange}
                  error={validationErrors.employeeId}
                  required
                  placeholder="ç¤¾å“¡IDã‚’å…¥åŠ›"
                  autoComplete="off"
                />
                <Input
                  label="éƒ¨ç½² (ä»»æ„)"
                  type="text"
                  name="department"
                  value={formData.department}
                  onChange={handleInputChange}
                  error={validationErrors.department}
                  placeholder="ä¾‹: æƒ…å ±å­¦éƒ¨"
                  autoComplete="organization"
                />
              </>
            )}

            <div className="form-row">
              <Input
                label="ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰"
                type="password"
                name="password"
                value={formData.password}
                onChange={handleInputChange}
                error={validationErrors.password}
                required
                placeholder="6æ–‡å­—ä»¥ä¸Šã§å…¥åŠ›"
                autoComplete="new-password"
              />

              <Input
                label="ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ç¢ºèª"
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleInputChange}
                error={validationErrors.confirmPassword}
                required
                placeholder="ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰ã‚’å†å…¥åŠ›"
                autoComplete="new-password"
              />
            </div>

            <Button
              type="submit"
              variant="primary"
              size="large"
              className="register-button"
              loading={isLoading}
            >
              æ–°è¦ç™»éŒ²
            </Button>
          </form>

          <div className="register-footer">
            <p className="register-help">
              æ—¢ã«ã‚¢ã‚«ã‚¦ãƒ³ãƒˆã‚’ãŠæŒã¡ã®å ´åˆã¯ã€
            </p>
            <button
              type="button"
              onClick={() => navigate('/login')}
              className="login-link"
            >
              ãƒ­ã‚°ã‚¤ãƒ³ã¯ã“ã¡ã‚‰
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RegisterPage;

import React, { useState, useEffect } from 'react';
import { Link, useSearchParams, useNavigate } from 'react-router-dom';
import { attendanceApi } from '../api/attendanceApi';
