import React, { useState, useEffect, useCallback } from 'react';
import useAuthStore from '../stores/authStore';
import { attendanceApi } from '../api/attendanceApi';
import './StudentPage.css';

const StudentPage = React.memo(() => {
  const { user, isAuthenticated } = useAuthStore();
  const [students, setStudents] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingStudent, setEditingStudent] = useState(null);

  // ãƒ•ã‚©ãƒ¼ãƒ çŠ¶æ…‹
  const [formData, setFormData] = useState({
    student_id: '',
    name: '',
    card_id: ''
  });

  useEffect(() => {
    if (isAuthenticated && user?.role === 'admin') {
      loadStudents();
    }
  }, [isAuthenticated, user, loadStudents]);

  const loadStudents = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await attendanceApi.getStudents(searchTerm);

      if (response.success) {
        setStudents(response.data.students);
      } else {
        setError('å­¦ç”Ÿãƒ‡ãƒ¼ã‚¿ã®èª­ã¿è¾¼ã¿ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }
    } catch (err) {
      setError('å­¦ç”Ÿãƒ‡ãƒ¼ã‚¿ã®èª­ã¿è¾¼ã¿ã«å¤±æ•—ã—ã¾ã—ãŸ');
    } finally {
      setIsLoading(false);
    }
  }, [searchTerm]);

  const handleSearch = (e) => {
    e.preventDefault();
    loadStudents();
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setIsLoading(true);
      setError(null);

      let response;
      if (editingStudent) {
        response = await attendanceApi.updateStudent(editingStudent.student_id, formData);
      } else {
        response = await attendanceApi.createStudent(formData);
      }

      if (response.success) {
        setShowAddForm(false);
        setEditingStudent(null);
        setFormData({ student_id: '', name: '', card_id: '' });
        await loadStudents();
      } else {
        setError(response.message || 'æ“ä½œã«å¤±æ•—ã—ã¾ã—ãŸ');
      }
    } catch (err) {
      setError('æ“ä½œã«å¤±æ•—ã—ã¾ã—ãŸ');
      // é–‹ç™ºç’°å¢ƒã§ã®ã¿ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°å‡ºåŠ›
      if (process.env.NODE_ENV === 'development') {
        // eslint-disable-next-line no-console
        console.error('å­¦ç”Ÿæ“ä½œã‚¨ãƒ©ãƒ¼:', err);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleEdit = (student) => {
    setEditingStudent(student);
    setFormData({
      student_id: student.student_id,
      name: student.name,
      card_id: student.card_id || ''
    });
    setShowAddForm(true);
  };

  const handleDelete = async (studentId) => {
    if (!window.confirm('ã“ã®å­¦ç”Ÿã‚’å‰Šé™¤ã—ã¾ã™ã‹ï¼Ÿ')) {
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      const response = await attendanceApi.deleteStudent(studentId);

      if (response.success) {
        await loadStudents();
      } else {
        setError(response.message || 'å‰Šé™¤ã«å¤±æ•—ã—ã¾ã—ãŸ');
      }
    } catch (err) {
      setError('å‰Šé™¤ã«å¤±æ•—ã—ã¾ã—ãŸ');
      // é–‹ç™ºç’°å¢ƒã§ã®ã¿ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°å‡ºåŠ›
      if (process.env.NODE_ENV === 'development') {
        // eslint-disable-next-line no-console
        console.error('å­¦ç”Ÿå‰Šé™¤ã‚¨ãƒ©ãƒ¼:', err);
      }
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    setShowAddForm(false);
    setEditingStudent(null);
    setFormData({ student_id: '', name: '', card_id: '' });
  };

  if (!isAuthenticated || user?.role !== 'admin') {
    return (
      <div className="student-page">
        <div className="access-denied">
          <h2>ã‚¢ã‚¯ã‚»ã‚¹æ‹’å¦</h2>
          <p>ã“ã®ãƒšãƒ¼ã‚¸ã«ã‚¢ã‚¯ã‚»ã‚¹ã™ã‚‹ã«ã¯ç®¡ç†è€…æ¨©é™ãŒå¿…è¦ã§ã™ã€‚</p>
        </div>
      </div>
    );
  }

  if (isLoading && students.length === 0) {
    return (
      <div className="student-page">
        <div className="loading">
          <div className="spinner" />
          <p>å­¦ç”Ÿãƒ‡ãƒ¼ã‚¿ã‚’èª­ã¿è¾¼ã¿ä¸­...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="student-page">
      <div className="student-container">
        <div className="student-header">
          <h1>å­¦ç”Ÿç®¡ç†</h1>
          <button
            className="btn btn--primary"
            onClick={() => setShowAddForm(true)}
            disabled={isLoading}
          >
            æ–°è¦å­¦ç”Ÿè¿½åŠ 
          </button>
        </div>

        {error && (
          <div className="error-message">
            {error}
            <button
              className="retry-button"
              onClick={loadStudents}
            >
              å†è©¦è¡Œ
            </button>
          </div>
        )}

        {/* æ¤œç´¢ãƒ•ã‚©ãƒ¼ãƒ  */}
        <div className="search-form">
          <form onSubmit={handleSearch}>
            <div className="search-input-group">
              <input
                type="text"
                placeholder="å­¦ç”Ÿåã€å­¦ç”ŸIDã€ã‚«ãƒ¼ãƒ‰IDã§æ¤œç´¢..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <button type="submit" className="btn btn--secondary">
                æ¤œç´¢
              </button>
            </div>
          </form>
        </div>

        {/* å­¦ç”Ÿè¿½åŠ ãƒ»ç·¨é›†ãƒ•ã‚©ãƒ¼ãƒ  */}
        {showAddForm && (
          <div className="student-form-overlay">
            <div className="student-form">
              <h2>{editingStudent ? 'å­¦ç”Ÿæƒ…å ±ç·¨é›†' : 'æ–°è¦å­¦ç”Ÿè¿½åŠ '}</h2>
              <form onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="student_id">å­¦ç”ŸID *</label>
                  <input
                    type="text"
                    id="student_id"
                    name="student_id"
                    value={formData.student_id}
                    onChange={handleInputChange}
                    required
                    disabled={editingStudent}
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="name">åå‰ *</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    className="form-input"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="card_id">ã‚«ãƒ¼ãƒ‰ID</label>
                  <input
                    type="text"
                    id="card_id"
                    name="card_id"
                    value={formData.card_id}
                    onChange={handleInputChange}
                    className="form-input"
                    placeholder="ã‚ªãƒ—ã‚·ãƒ§ãƒ³"
                  />
                </div>

                <div className="form-actions">
                  <button
                    type="button"
                    onClick={handleCancel}
                    className="btn btn--secondary"
                    disabled={isLoading}
                  >
                    ã‚­ãƒ£ãƒ³ã‚»ãƒ«
                  </button>
                  <button
                    type="submit"
                    className="btn btn--primary"
                    disabled={isLoading}
                  >
                    {editingStudent ? 'æ›´æ–°' : 'è¿½åŠ '}
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* å­¦ç”Ÿä¸€è¦§ */}
        <div className="student-list">
          {students.length === 0 ? (
            <div className="no-students">
              <p>å­¦ç”ŸãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“</p>
            </div>
          ) : (
            <div className="student-grid">
              {students.map((student) => (
                <div key={student.student_id} className="student-card">
                  <div className="student-info">
                    <h3>{student.name}</h3>
                    <p className="student-id">ID: {student.student_id}</p>
                    {student.card_id && (
                      <p className="card-id">ã‚«ãƒ¼ãƒ‰ID: {student.card_id}</p>
                    )}
                  </div>

                  <div className="student-actions">
                    <button
                      onClick={() => handleEdit(student)}
                      className="btn btn--small btn--secondary"
                      disabled={isLoading}
                    >
                      ç·¨é›†
                    </button>
                    <button
                      onClick={() => handleDelete(student.student_id)}
                      className="btn btn--small btn--danger"
                      disabled={isLoading}
                    >
                      å‰Šé™¤
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );

});

