/**
 * Service Workerã‚’ç™»éŒ²ã™ã‚‹
 */
export const registerServiceWorker = async () => {
  if ('serviceWorker' in navigator) {
    try {
      const registration = await navigator.serviceWorker.register('/sw.js');
      console.log('Service Workerç™»éŒ²æˆåŠŸ:', registration);

      // æ›´æ–°ã®ç¢ºèª
      registration.addEventListener('updatefound', () => {
        const newWorker = registration.installing;
        if (newWorker) {
          newWorker.addEventListener('statechange', () => {
            if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
              // æ–°ã—ã„ãƒãƒ¼ã‚¸ãƒ§ãƒ³ãŒåˆ©ç”¨å¯èƒ½
              if (confirm('æ–°ã—ã„ãƒãƒ¼ã‚¸ãƒ§ãƒ³ãŒåˆ©ç”¨å¯èƒ½ã§ã™ã€‚æ›´æ–°ã—ã¾ã™ã‹ï¼Ÿ')) {
                newWorker.postMessage({ action: 'skipWaiting' });
                window.location.reload();
              }
            }
          });
        }
      });

      return registration;
    } catch (error) {
      console.error('Service Workerç™»éŒ²å¤±æ•—:', error);
      throw error;
    }
  } else {
    console.log('Service Workerã¯ã‚µãƒãƒ¼ãƒˆã•ã‚Œã¦ã„ã¾ã›ã‚“');
    return null;
  }
};

/**
 * PWAã®ã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ãƒ—ãƒ­ãƒ³ãƒ—ãƒˆã‚’ç®¡ç†ã™ã‚‹
 */
export class PWAInstallManager {
  constructor() {
    this.deferredPrompt = null;
    this.isInstalled = false;
    this.init();
  }

  init() {
    // ã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ãƒ—ãƒ­ãƒ³ãƒ—ãƒˆã®ã‚¤ãƒ™ãƒ³ãƒˆã‚’ã‚­ãƒ£ãƒƒãƒ
    window.addEventListener('beforeinstallprompt', (e) => {
      e.preventDefault();
      this.deferredPrompt = e;
      this.showInstallButton();
    });

    // ã‚¢ãƒ—ãƒªãŒã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ã•ã‚ŒãŸã‹ãƒã‚§ãƒƒã‚¯
    window.addEventListener('appinstalled', () => {
      this.isInstalled = true;
      this.hideInstallButton();
      console.log('PWAãŒã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ã•ã‚Œã¾ã—ãŸ');
    });

    // ã‚¹ã‚¿ãƒ³ãƒ‰ã‚¢ãƒ­ãƒ³ãƒ¢ãƒ¼ãƒ‰ã‹ãƒã‚§ãƒƒã‚¯
    if (window.matchMedia('(display-mode: standalone)').matches) {
      this.isInstalled = true;
    }
  }

  showInstallButton() {
    const installButton = document.getElementById('pwa-install-button');
    if (installButton) {
      installButton.style.display = 'block';
    }
  }

  hideInstallButton() {
    const installButton = document.getElementById('pwa-install-button');
    if (installButton) {
      installButton.style.display = 'none';
    }
  }

  async install() {
    if (!this.deferredPrompt) {
      return false;
    }

    try {
      this.deferredPrompt.prompt();
      const { outcome } = await this.deferredPrompt.userChoice;

      if (outcome === 'accepted') {
        console.log('PWAã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ãŒæ‰¿èªã•ã‚Œã¾ã—ãŸ');
      } else {
        console.log('PWAã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ãŒæ‹’å¦ã•ã‚Œã¾ã—ãŸ');
      }

      this.deferredPrompt = null;
      this.hideInstallButton();
      return outcome === 'accepted';
    } catch (error) {
      console.error('PWAã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }
}

/**
 * ã‚ªãƒ•ãƒ©ã‚¤ãƒ³çŠ¶æ…‹ã‚’æ¤œçŸ¥ã™ã‚‹
 */
export class OfflineManager {
  constructor() {
    this.isOnline = navigator.onLine;
    this.init();
  }

  init() {
    window.addEventListener('online', () => {
      this.isOnline = true;
      this.showOnlineMessage();
    });

    window.addEventListener('offline', () => {
      this.isOnline = false;
      this.showOfflineMessage();
    });
  }

  showOnlineMessage() {
    this.showNotification('ã‚¤ãƒ³ã‚¿ãƒ¼ãƒãƒƒãƒˆæŽ¥ç¶šãŒå¾©æ—§ã—ã¾ã—ãŸ', 'success');
  }

  showOfflineMessage() {
    this.showNotification('ã‚ªãƒ•ãƒ©ã‚¤ãƒ³ã§ã™ã€‚ä¸€éƒ¨æ©Ÿèƒ½ãŒåˆ¶é™ã•ã‚Œã¾ã™ã€‚', 'warning');
  }

  showNotification(message, type = 'info') {
    // é€šçŸ¥ã‚’è¡¨ç¤ºã™ã‚‹ãƒ­ã‚¸ãƒƒã‚¯
    const notification = document.createElement('div');
    notification.className = `notification notification--${type}`;
    notification.textContent = message;

    document.body.appendChild(notification);

    setTimeout(() => {
      notification.remove();
    }, 3000);
  }
}

/**
 * ãƒ—ãƒƒã‚·ãƒ¥é€šçŸ¥ã‚’ç®¡ç†ã™ã‚‹
 */
export class NotificationManager {
  constructor() {
    this.permission = Notification.permission;
  }

  async requestPermission() {
    if (!('Notification' in window)) {
      console.log('ã“ã®ãƒ–ãƒ©ã‚¦ã‚¶ã¯é€šçŸ¥ã‚’ã‚µãƒãƒ¼ãƒˆã—ã¦ã„ã¾ã›ã‚“');
      return false;
    }

    if (this.permission === 'granted') {
      return true;
    }

    if (this.permission === 'denied') {
      console.log('é€šçŸ¥ãŒæ‹’å¦ã•ã‚Œã¦ã„ã¾ã™');
      return false;
    }

    try {
      const permission = await Notification.requestPermission();
      this.permission = permission;
      return permission === 'granted';
    } catch (error) {
      console.error('é€šçŸ¥è¨±å¯ãƒªã‚¯ã‚¨ã‚¹ãƒˆã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }

  showNotification(title, options = {}) {
    if (this.permission !== 'granted') {
      console.log('é€šçŸ¥ã®è¨±å¯ãŒã‚ã‚Šã¾ã›ã‚“');
      return null;
    }

    const defaultOptions = {
      icon: '/logo192.png',
      badge: '/logo192.png',
      tag: 'attendance-notification',
      requireInteraction: false,
      ...options,
    };

    try {
      const notification = new Notification(title, defaultOptions);

      notification.onclick = () => {
        window.focus();
        notification.close();
      };

      return notification;
    } catch (error) {
      console.error('é€šçŸ¥è¡¨ç¤ºã‚¨ãƒ©ãƒ¼:', error);
      return null;
    }
  }

  showAttendanceReminder() {
    return this.showNotification('å‡ºå‹¤æ™‚é–“ã§ã™', {
      body: 'å‡ºå‹¤è¨˜éŒ²ã‚’ãŠå¿˜ã‚Œãªãï¼',
      icon: '/logo192.png',
    });
  }
}

/**
 * ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰åŒæœŸã‚’ç®¡ç†ã™ã‚‹
 */
export class BackgroundSyncManager {
  constructor() {
    this.isSupported = 'serviceWorker' in navigator && 'sync' in window.ServiceWorkerRegistration.prototype;
  }

  async registerSync(tag, data = {}) {
    if (!this.isSupported) {
      console.log('ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰åŒæœŸã¯ã‚µãƒãƒ¼ãƒˆã•ã‚Œã¦ã„ã¾ã›ã‚“');
      return false;
    }

    try {
      const registration = await navigator.serviceWorker.ready;
      await registration.sync.register(tag);

      // ãƒ‡ãƒ¼ã‚¿ã‚’IndexedDBã«ä¿å­˜
      await this.saveSyncData(tag, data);

      console.log(`ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰åŒæœŸã‚’ç™»éŒ²ã—ã¾ã—ãŸ: ${tag}`);
      return true;
    } catch (error) {
      console.error('ãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰åŒæœŸç™»éŒ²ã‚¨ãƒ©ãƒ¼:', error);
      return false;
    }
  }

  async saveSyncData(tag, data) {
    // IndexedDBã«ãƒ‡ãƒ¼ã‚¿ã‚’ä¿å­˜ã™ã‚‹ãƒ­ã‚¸ãƒƒã‚¯
    // å®Ÿè£…ã¯å¿…è¦ã«å¿œã˜ã¦è¿½åŠ 
    console.log(`åŒæœŸãƒ‡ãƒ¼ã‚¿ã‚’ä¿å­˜: ${tag}`, data);
  }
}

