import { create } from 'zustand';
import { attendanceApi } from '../api/attendanceApi';

const useAuthStore = create((set, get) => ({
  user: null,
  isAuthenticated: false,
  isLoading: true,
  viewMode: null, // 'student' | null

  setLoading: (loading) => set({ isLoading: loading }),

  // è¡¨ç¤ºãƒ¢ãƒ¼ãƒ‰åˆ‡ã‚Šæ›¿ãˆã‚¢ã‚¯ã‚·ãƒ§ãƒ³
  toggleViewMode: () => {
    const { user, viewMode } = get();
    // å­¦ç”Ÿãƒ¦ãƒ¼ã‚¶ãƒ¼ã¯åˆ‡ã‚Šæ›¿ãˆä¸å¯
    if (!user || user.role === 'student') return;

    set({ viewMode: viewMode === 'student' ? null : 'student' });
  },

  // ãƒ­ã‚°ã‚¤ãƒ³ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
  login: async (email, password) => {
    try {
      const response = await attendanceApi.login(email, password);

      if (response.success) {
        const { user } = response.data;
        set({ user, isAuthenticated: true, viewMode: null });
        return { success: true };
      } else {
        set({ user: null, isAuthenticated: false, viewMode: null });
        return { success: false, message: response.message };
      }
    } catch (error) {
      console.error('Login failed:', error);
      set({ user: null, isAuthenticated: false, viewMode: null });
      return { success: false, message: 'ãƒ­ã‚°ã‚¤ãƒ³ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }
  },

  // æ–°è¦ç™»éŒ²ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
  register: async (userData) => {
    try {
      const response = await attendanceApi.register(userData);

      if (response.success) {
        const { user } = response.data;
        set({ user, isAuthenticated: true, viewMode: null });
        return { success: true };
      } else {
        set({ user: null, isAuthenticated: false, viewMode: null });
        return { success: false, message: response.message || 'ç™»éŒ²ã«å¤±æ•—ã—ã¾ã—ãŸ' };
      }
    } catch (error) {
      console.error('Registration failed:', error);
      set({ user: null, isAuthenticated: false, viewMode: null });
      return { success: false, message: error.message || 'ç™»éŒ²ã«å¤±æ•—ã—ã¾ã—ãŸ' };
    }
  },

  // ãƒ­ã‚°ã‚¢ã‚¦ãƒˆã‚¢ã‚¯ã‚·ãƒ§ãƒ³
  logout: async () => {
    try {
      await attendanceApi.logout();
    } catch (error) {
      console.error('Logout failed:', error);
    } finally {
      set({ user: null, isAuthenticated: false, viewMode: null });
    }
  },

  // ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ã‚’ã‚»ãƒƒãƒˆã™ã‚‹ã‚¢ã‚¯ã‚·ãƒ§ãƒ³
  setUser: (user) => {
    set({ user, isAuthenticated: !!user });
  },

  // åˆæœŸåŒ–ãƒã‚§ãƒƒã‚¯ï¼ˆCookieã«ã‚ˆã‚‹è‡ªå‹•ãƒ­ã‚°ã‚¤ãƒ³ç¢ºèªç”¨ï¼‰
  checkAuth: async () => {
    try {
      set({ isLoading: true });
      const response = await attendanceApi.getAuthUser();
      if (response.success) {
        set({ user: response.data.user, isAuthenticated: true });
      }
    } catch (error) {
      // èªè¨¼åˆ‡ã‚Œãªã©ã¯ã“ã“ã§ç„¡è¦–ã—ã¦æœªãƒ­ã‚°ã‚¤ãƒ³çŠ¶æ…‹ã«ã™ã‚‹
      set({ user: null, isAuthenticated: false });
    } finally {
      set({ isLoading: false });
    }
  }
}));

export default useAuthStore;

import { create } from 'zustand';

const useToastStore = create((set) => ({
    toasts: [],

    addToast: (message, type = 'info', duration = 3000) => {
        const id = Math.random().toString(36).substr(2, 9);
        set((state) => ({
            toasts: [...state.toasts, { id, message, type, duration }]
        }));

        if (duration > 0) {
            setTimeout(() => {
                set((state) => ({
                    toasts: state.toasts.filter((t) => t.id !== id)
                }));
            }, duration);
        }
    },

    removeToast: (id) => {
        set((state) => ({
            toasts: state.toasts.filter((t) => t.id !== id)
        }));
    }
}));

export default useToastStore;

