/**
 * [è¿½åŠ ] æ¸¡ã•ã‚ŒãŸå€¤ã‚’å®‰å…¨ã«Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã«å¤‰æ›ã™ã‚‹ãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
 * @param {*} date - Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã€æ–‡å­—åˆ—ã€æ•°å€¤ã€ã¾ãŸã¯null
 * @returns {Date | null} æœ‰åŠ¹ãªDateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆã€ã¾ãŸã¯ç„¡åŠ¹ãªå ´åˆã¯null
 */
const ensureDateObject = (date) => {
  if (Object.prototype.toString.call(date) === '[object Date]') {
    // æ—¢ã«Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
    return date;
  }

  if (typeof date === 'string' || typeof date === 'number') {
    // æ–‡å­—åˆ—ã¾ãŸã¯æ•°å€¤ï¼ˆã‚¿ã‚¤ãƒ ã‚¹ã‚¿ãƒ³ãƒ—ï¼‰ã‹ã‚‰å¤‰æ›
    const d = new Date(date);
    if (!isNaN(d.getTime())) {
      return d; // æœ‰åŠ¹ãªæ—¥ä»˜
    }
  }

  // null, undefined, ã¾ãŸã¯ç„¡åŠ¹ãªæ—¥ä»˜
  return null;
};

/**
 * YYYY-MM-DD HH:MM:SS å½¢å¼ã®æ–‡å­—åˆ—ã‚’è¿”ã™
 */
export const formatDate = (date, formatStr) => {
  const d = ensureDateObject(date); // [ä¿®æ­£] å®‰å…¨ãªå¤‰æ›ã‚’è¡Œã†

  // [ä¿®æ­£] ç„¡åŠ¹ãªæ—¥ä»˜ã®å ´åˆã¯ç©ºæ–‡å­—ã‚’è¿”ã™
  if (!d) return '';

  formatStr = formatStr.replace(/YYYY/g, d.getFullYear());
  formatStr = formatStr.replace(/MM/g, (d.getMonth() + 1).toString().padStart(2, '0'));
  formatStr = formatStr.replace(/DD/g, d.getDate().toString().padStart(2, '0'));
  formatStr = formatStr.replace(/HH/g, d.getHours().toString().padStart(2, '0'));
  formatStr = formatStr.replace(/MI/g, d.getMinutes().toString().padStart(2, '0'));
  formatStr = formatStr.replace(/SS/g, d.getSeconds().toString().padStart(2, '0'));
  return formatStr;
};

/**
 * HH:MM å½¢å¼ã®æ–‡å­—åˆ—ã‚’è¿”ã™
 */
export const formatTime = (timeStr) => {
  if (!timeStr) return '';
  // "HH:MM:SS" ã¾ãŸã¯ "HH:MM" å½¢å¼ã‚’æƒ³å®š
  return timeStr.split(':').slice(0, 2).join(':');
};

/**
 * ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼è¡¨ç¤ºç”¨ã®æ—¥ä»˜é…åˆ—ã‚’ç”Ÿæˆ
 */
export const getMonthDays = (year, month) => {
  const date = new Date(year, month, 1);
  const days = [];

  // æ›œæ—¥ã«åˆã‚ã›ã¦ç©ºç™½ã‚’æŒ¿å…¥
  const firstDayOfMonth = date.getDay(); // 0 (æ—¥) - 6 (åœŸ)
  for (let i = 0; i < firstDayOfMonth; i++) {
    days.push({ day: null, date: null, isCurrentMonth: false });
  }

  // æœˆã®æ—¥ä»˜ã‚’è¿½åŠ 
  while (date.getMonth() === month) {
    days.push({
      day: date.getDate(),
      date: new Date(date), // Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
      isCurrentMonth: true,
    });
    date.setDate(date.getDate() + 1);
  }

  // é€±ã®æ®‹ã‚Šã‚’åŸ‹ã‚ã‚‹
  while (days.length % 7 !== 0) {
    days.push({ day: null, date: null, isCurrentMonth: false });
  }

  return days;
};

/**
 * Dateã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆãŒä»Šæ—¥ã‹ã©ã†ã‹ã‚’åˆ¤å®š
 */
export const isToday = (date) => {
  const d = ensureDateObject(date); // [ä¿®æ­£] å®‰å…¨ãªå¤‰æ›ã‚’è¡Œã†

  // [ä¿®æ­£] ç„¡åŠ¹ãªæ—¥ä»˜ã®å ´åˆã¯falseã‚’è¿”ã™
  if (!d) return false;

  const today = new Date();
  return (
    d.getDate() === today.getDate() &&
    d.getMonth() === today.getMonth() &&
    d.getFullYear() === today.getFullYear()
  );
};

/**
 * ãƒ¢ã‚¸ãƒ¥ãƒ¼ãƒ«é€£æºãƒã‚§ãƒƒã‚¯ç”¨ãƒ¦ãƒ¼ãƒ†ã‚£ãƒªãƒ†ã‚£
 * å„ãƒ¢ã‚¸ãƒ¥ãƒ¼ãƒ«ãŒæ­£ã—ãé€£æºã—ã¦ã„ã‚‹ã‹ç¢ºèªã™ã‚‹ãŸã‚ã®ãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
 */

/**
 * APIå‘¼ã³å‡ºã—ã®å®‰å…¨æ€§ãƒã‚§ãƒƒã‚¯
 * @param {string} functionName - é–¢æ•°å
 * @param {Object} params - ãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
 * @param {Array<string>} requiredParams - å¿…é ˆãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã®é…åˆ—
 */
export const validateApiParams = (functionName, params, requiredParams = []) => {
  const missingParams = requiredParams.filter(param => {
    const value = params[param];
    return value === undefined || value === null || value === '';
  });

  if (missingParams.length > 0) {
    throw new Error(
      `${functionName}: å¿…é ˆãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ãŒä¸è¶³ã—ã¦ã„ã¾ã™: ${missingParams.join(', ')}`
    );
  }
};

/**
 * ãƒ¦ãƒ¼ã‚¶ãƒ¼IDã®æ¤œè¨¼
 * @param {number|string} userId - ãƒ¦ãƒ¼ã‚¶ãƒ¼ID
 * @param {string} context - ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆï¼ˆã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ç”¨ï¼‰
 */
export const validateUserId = (userId, context = 'æ“ä½œ') => {
  if (!userId) {
    throw new Error(`${context}ã«ã¯ãƒ¦ãƒ¼ã‚¶ãƒ¼IDãŒå¿…è¦ã§ã™`);
  }
  if (typeof userId !== 'number' && typeof userId !== 'string') {
    throw new Error(`${context}ã®ãƒ¦ãƒ¼ã‚¶ãƒ¼IDãŒç„¡åŠ¹ã§ã™`);
  }
};

/**
 * èªè¨¼çŠ¶æ…‹ã®ãƒã‚§ãƒƒã‚¯
 * @param {Object} authState - èªè¨¼çŠ¶æ…‹ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
 * @param {boolean} requireAuth - èªè¨¼ãŒå¿…è¦ã‹ã©ã†ã‹
 */
export const validateAuthState = (authState, requireAuth = true) => {
  if (requireAuth && !authState.isAuthenticated) {
    throw new Error('ã“ã®æ“ä½œã«ã¯ãƒ­ã‚°ã‚¤ãƒ³ãŒå¿…è¦ã§ã™');
  }
  if (requireAuth && !authState.user?.id) {
    throw new Error('ãƒ¦ãƒ¼ã‚¶ãƒ¼æƒ…å ±ãŒå–å¾—ã§ãã¾ã›ã‚“ã§ã—ãŸ');
  }
};

/**
 * APIãƒ¬ã‚¹ãƒãƒ³ã‚¹ã®æ¤œè¨¼
 * @param {Object} response - APIãƒ¬ã‚¹ãƒãƒ³ã‚¹
 * @param {string} context - ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆï¼ˆã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ç”¨ï¼‰
 */
export const validateApiResponse = (response, context = 'APIå‘¼ã³å‡ºã—') => {
  if (!response) {
    throw new Error(`${context}: ãƒ¬ã‚¹ãƒãƒ³ã‚¹ãŒã‚ã‚Šã¾ã›ã‚“`);
  }
  if (response.success === false) {
    throw new Error(response.message || `${context}ã«å¤±æ•—ã—ã¾ã—ãŸ`);
  }
};

/**
 * ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒªãƒ³ã‚°ã®ãƒ˜ãƒ«ãƒ‘ãƒ¼
 * @param {Error} error - ã‚¨ãƒ©ãƒ¼ã‚ªãƒ–ã‚¸ã‚§ã‚¯ãƒˆ
 * @param {string} context - ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ
 * @param {Function} onError - ã‚¨ãƒ©ãƒ¼å‡¦ç†ã‚³ãƒ¼ãƒ«ãƒãƒƒã‚¯
 */
export const handleApiError = (error, context = 'æ“ä½œ', onError = null) => {
  const errorMessage = error.message || `${context}ä¸­ã«ã‚¨ãƒ©ãƒ¼ãŒç™ºç”Ÿã—ã¾ã—ãŸ`;

  if (onError && typeof onError === 'function') {
    onError(errorMessage, error);
  }

  // é–‹ç™ºç’°å¢ƒã§ã®ã¿ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°å‡ºåŠ›
  if (process.env.NODE_ENV === 'development') {
    // eslint-disable-next-line no-console
    console.error(`${context}ã‚¨ãƒ©ãƒ¼:`, error);
  }

  return errorMessage;
};

/**
 * å®‰å…¨ãªAPIå‘¼ã³å‡ºã—ã®ãƒ©ãƒƒãƒ‘ãƒ¼
 * @param {Function} apiCall - APIå‘¼ã³å‡ºã—é–¢æ•°
 * @param {Object} params - ãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿
 * @param {Array<string>} requiredParams - å¿…é ˆãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿
 * @param {string} context - ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ
 */
export const safeApiCall = async (apiCall, params, requiredParams = [], context = 'APIå‘¼ã³å‡ºã—') => {
  try {
    // å¿…é ˆãƒ‘ãƒ©ãƒ¡ãƒ¼ã‚¿ã®æ¤œè¨¼
    validateApiParams(apiCall.name || context, params, requiredParams);

    // APIå‘¼ã³å‡ºã—
    const response = await apiCall(params);

    // ãƒ¬ã‚¹ãƒãƒ³ã‚¹ã®æ¤œè¨¼
    validateApiResponse(response, context);

    return response;
  } catch (error) {
    handleApiError(error, context);
    throw error;
  }
};
